// App.js
// 전체 앱 엔트리포인트
import React, { useState, useEffect } from 'react';
import ChatComponent from './components/ChatComponent';
import DatabaseManager from './components/DatabaseManager';
import MonitoringComponent from './components/MonitoringComponent';
import SlowQueryComponent from './components/SlowQueryComponent';
import AwsIntegrationComponent from './components/AwsIntegrationComponent';
import OpenAIManager from './components/OpenAIManager';
import PlaybookComponent from './components/PlaybookComponent';

function App() {
  const [currentView, setCurrentView] = useState('chat');
  const [databases, setDatabases] = useState([]);
  const [selectedDb, setSelectedDb] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAwsSubMenu, setShowAwsSubMenu] = useState(false);

  // 네비게이션 메뉴
  const menuItems = [
    { id: 'chat', label: '💬 AI 채팅', icon: '💬' },
    { id: 'databases', label: '🗄️ DB 관리', icon: '🗄️' },
    { id: 'openai', label: '🔑 OpenAI 설정', icon: '🔑' },
    { id: 'playbooks', label: '📖 플레이북', icon: '📖' },
    { id: 'monitoring', label: '📊 모니터링', icon: '📊' },
    { id: 'slowquery', label: '🐌 슬로우 쿼리', icon: '🐌' },
    { id: 'aws', label: '☁️ AWS 통합', icon: '☁️' },
    { id: 'aws-resources', label: '☁️ AWS 리소스 조회', icon: '☁️',
      children: [
        { id: 'aws-rds', label: 'RDS 조회', icon: '🗄️' },
        { id: 'aws-cloudwatch', label: 'CloudWatch 조회', icon: '📊' }
      ]
    }
  ];

  // DB 목록 가져오기
  const fetchDatabases = async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/databases');
      if (response.ok) {
        const data = await response.json();
        setDatabases(data.databases || []);

        // 첫 번째 DB를 자동 선택 (있는 경우)
        if (data.databases && data.databases.length > 0 && !selectedDb) {
          setSelectedDb(data.databases[0].name);
        }
      } else {
        console.error('DB 목록 가져오기 실패');
      }
    } catch (error) {
      console.error('DB 목록 요청 오류:', error);
      setError('데이터베이스 목록을 가져오는 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  // DB 변경 처리
  const handleDatabaseChange = () => {
    fetchDatabases();
  };

  // DB 삭제 처리
  const handleDatabaseDelete = (dbName) => {
    setDatabases(prev => prev.filter(db => db.name !== dbName));
    if (selectedDb === dbName) {
      setSelectedDb('');
    }
  };

  // DB 추가 처리
  const handleDatabaseAdd = () => {
    fetchDatabases();
  };

  // 초기 로드
  useEffect(() => {
    fetchDatabases();
  }, []);

  // 현재 뷰에 따른 컴포넌트 렌더링
  const renderCurrentView = () => {
    const commonProps = {
      selectedDb,
      databases,
      onDatabaseChange: handleDatabaseChange
    };

    switch (currentView) {
      case 'chat':
        return (
          <ChatComponent
            {...commonProps}
            onDbChange={setSelectedDb}
          />
        );
      case 'databases':
        return (
          <DatabaseManager
            {...commonProps}
            onDatabaseDelete={handleDatabaseDelete}
            onDatabaseAdd={handleDatabaseAdd}
          />
        );
      case 'openai':
        return <OpenAIManager />;
      case 'playbooks':
        return <PlaybookComponent {...commonProps} />;
      case 'monitoring':
        return <MonitoringComponent {...commonProps} />;
      case 'slowquery':
        return <SlowQueryComponent {...commonProps} />;
      case 'aws':
        return <AwsIntegrationComponent {...commonProps} />;
      default:
        return <ChatComponent {...commonProps} />;
    }
  };

  // 로딩 상태
  if (loading && databases.length === 0) {
    return (
      <div className="app-loading">
        <div className="loading-spinner"></div>
        <p>데이터베이스 정보를 불러오는 중...</p>
      </div>
    );
  }

  return (
    <div className="app">
      {/* 사이드바 네비게이션 */}
      <div className="sidebar">
        <div className="sidebar-header">
          <h1>🚀 DB Agent</h1>
          <p>SRE/DBA용 AI 도구</p>
        </div>

        <nav className="sidebar-nav">
          {menuItems.map(item => (
            item.children ? (
              <div key={item.id}>
                <button
                  onClick={() => setShowAwsSubMenu(!showAwsSubMenu)}
                  className={`nav-item ${currentView.startsWith('aws-') ? 'active' : ''}`}
                >
                  <span className="nav-icon">{item.icon}</span>
                  <span className="nav-label">{item.label}</span>
                </button>
                {showAwsSubMenu && (
                  <div className="submenu">
                    {item.children.map(sub => (
                      <button
                        key={sub.id}
                        onClick={() => setCurrentView(sub.id)}
                        className={`nav-item sub ${currentView === sub.id ? 'active' : ''}`}
                      >
                        <span className="nav-icon">{sub.icon}</span>
                        <span className="nav-label">{sub.label}</span>
                      </button>
                    ))}
                  </div>
                )}
              </div>
            ) : (
              <button
                key={item.id}
                onClick={() => setCurrentView(item.id)}
                className={`nav-item ${currentView === item.id ? 'active' : ''}`}
              >
                <span className="nav-icon">{item.icon}</span>
                <span className="nav-label">{item.label}</span>
              </button>
            )
          ))}
        </nav>

        {/* DB 선택 (채팅, 모니터링, 슬로우 쿼리, 플레이북에서만 표시) */}
        {['chat', 'monitoring', 'slowquery', 'playbooks'].includes(currentView) && (
          <div className="db-selector">
            <label htmlFor="global-db-select">현재 DB:</label>
            <select
              id="global-db-select"
              value={selectedDb}
              onChange={(e) => setSelectedDb(e.target.value)}
              className="db-select"
            >
              <option value="">DB를 선택하세요</option>
              {databases.map(db => (
                <option key={db.name} value={db.name}>
                  {db.name}
                </option>
              ))}
            </select>
          </div>
        )}

        {/* 상태 표시 */}
        <div className="status-section">
          <div className="status-item">
            <span className="status-label">연결된 DB:</span>
            <span className="status-value">{databases.length}개</span>
          </div>
          {selectedDb && (
            <div className="status-item">
              <span className="status-label">선택된 DB:</span>
              <span className="status-value">{selectedDb}</span>
            </div>
          )}
        </div>
      </div>

      {/* 메인 콘텐츠 영역 */}
      <div className="main-content">
        {/* 헤더 */}
        <header className="main-header">
          <div className="header-left">
            <h2>{menuItems.find(item => item.id === currentView)?.label}</h2>
            {selectedDb && ['chat', 'monitoring', 'slowquery', 'playbooks'].includes(currentView) && (
              <span className="current-db">DB: {selectedDb}</span>
            )}
          </div>

          <div className="header-actions">
            {/* 새로고침 버튼 */}
            <button 
              onClick={fetchDatabases}
              className="btn btn-outline"
              title="새로고침"
            >
              🔄
            </button>

            {/* 도움말 버튼 */}
            <button 
              onClick={() => alert('DB Agent 사용법:\n\n1. DB 관리에서 데이터베이스 연결을 추가하세요\n2. OpenAI 설정에서 API 키를 등록하고 선택하세요\n3. AI 채팅에서 자연어로 DB를 질의하세요\n4. 플레이북으로 자동화된 작업을 실행하세요\n5. 모니터링에서 실시간 상태를 확인하세요\n6. 슬로우 쿼리에서 성능 문제를 분석하세요\n7. AWS 통합에서 CloudWatch 메트릭을 확인하세요')}
              className="btn btn-outline"
              title="도움말"
            >
              ❓
            </button>
          </div>
        </header>

        {/* 에러 메시지 */}
        {error && (
          <div className="error-banner">
            <span>⚠️ {error}</span>
            <button onClick={() => setError('')} className="close-btn">✕</button>
          </div>
        )}

        {/* 콘텐츠 영역 */}
        <div className="content-area">
          {renderCurrentView()}
        </div>
      </div>
    </div>
  );
}

export default App;
