// AwsIntegrationComponent.js
// AWS Ïù∏Ï¶ù, RDS/CloudWatch Ïó∞Îèô, ÏÉÅÌÉú ÌëúÏãú
import React, { useState, useEffect, useCallback } from 'react';

function AwsIntegrationComponent({ selectedDb, databases }) {
  const [credentialsList, setCredentialsList] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const [authType, setAuthType] = useState('accessKey');
  const [accessKey, setAccessKey] = useState('');
  const [secretKey, setSecretKey] = useState('');
  const [sessionToken, setSessionToken] = useState('');
  const [ssoStartUrl, setSsoStartUrl] = useState('');
  const [ssoRegion, setSsoRegion] = useState('ap-northeast-2');
  const [ssoAccountId, setSsoAccountId] = useState('');
  const [ssoRoleName, setSsoRoleName] = useState('');
  const [awsRegion, setAwsRegion] = useState('ap-northeast-2');
  const [cloudwatchNamespace, setCloudwatchNamespace] = useState('AWS/RDS');
  const [isConnected, setIsConnected] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('disconnected');
  const [rdsInstances, setRdsInstances] = useState([]);
  const [cloudwatchMetrics, setCloudwatchMetrics] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const awsRegions = [
    { value: 'us-east-1', label: 'US East (N. Virginia)' },
    { value: 'us-east-2', label: 'US East (Ohio)' },
    { value: 'us-west-1', label: 'US West (N. California)' },
    { value: 'us-west-2', label: 'US West (Oregon)' },
    { value: 'ap-northeast-1', label: 'Asia Pacific (Tokyo)' },
    { value: 'ap-northeast-2', label: 'Asia Pacific (Seoul)' },
    { value: 'ap-southeast-1', label: 'Asia Pacific (Singapore)' },
    { value: 'ap-southeast-2', label: 'Asia Pacific (Sydney)' },
    { value: 'eu-west-1', label: 'Europe (Ireland)' },
    { value: 'eu-central-1', label: 'Europe (Frankfurt)' }
  ];

  // Ïù∏Ï¶ù Î∞©Ïãù ÏòµÏÖò
  const authTypes = [
    { value: 'accessKey', label: 'Access Key' },
    { value: 'iamRole', label: 'IAM Role (EC2/ECS/EKS)' },
    { value: 'sso', label: 'AWS SSO' }
  ];

  // Î¶¨Ïä§Ìä∏ Î∂àÎü¨Ïò§Í∏∞
  const loadCredentials = async () => {
    const res = await fetch('/aws/credentials');
    if (res.ok) {
      const list = await res.json();
      setCredentialsList(list);
      const active = list.find(c => c.is_active);
      if (active) {
        setSelectedId(active.id);
        setAccessKey(active.access_key);
        setSecretKey('********');
        setSessionToken('');
        setAwsRegion(active.region || 'ap-northeast-2');
        setConnectionStatus('connected');
        setIsConnected(true);
      } else {
        setSelectedId(null);
        setConnectionStatus('disconnected');
        setIsConnected(false);
      }
    }
  };

  useEffect(() => {
    loadCredentials();
  }, []);

  // RDS Ïù∏Ïä§ÌÑ¥Ïä§ Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchRdsInstances = useCallback(async () => {
    if (!isConnected) return;

    setLoading(true);
    try {
      const response = await fetch('/aws/rds-instances');
      if (response.ok) {
        const data = await response.json();
        setRdsInstances(data.instances || []);
      } else {
        console.error('RDS Ïù∏Ïä§ÌÑ¥Ïä§ Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®');
      }
    } catch (error) {
      console.error('RDS Ïù∏Ïä§ÌÑ¥Ïä§ ÏöîÏ≤≠ Ïò§Î•ò:', error);
    } finally {
      setLoading(false);
    }
  }, [isConnected]);

  // Ïó∞Í≤∞ ÏÑ±Í≥µ Ïãú RDS Ïù∏Ïä§ÌÑ¥Ïä§ ÏûêÎèô Ï°∞Ìöå
  useEffect(() => {
    if (isConnected) {
      fetchRdsInstances();
    }
  }, [isConnected, fetchRdsInstances]);

  // Îì±Î°ù
  const handleSave = async () => {
    setError(''); setSuccess(''); setLoading(true);
    const payload = {
      name: accessKey.slice(0, 8) + '...',
      access_key: accessKey,
      secret_key: secretKey,
      session_token: sessionToken,
      region: awsRegion,
      is_active: true
    };
    const res = await fetch('/aws/credentials', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(payload)
    });
    if (res.ok) {
      setSuccess('ÏÑ§Ï†ïÏù¥ Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§!');
      await loadCredentials();
    } else {
      setError('Ï†ÄÏû• Ïã§Ìå®');
    }
    setLoading(false);
  };

  // Ïó∞Í≤∞ ÌÖåÏä§Ìä∏
  const handleTest = async () => {
    setLoading(true); setError(''); setSuccess('');
    const cred = credentialsList.find(c => c.id === selectedId);
    if (!cred) { setError('ÏÑ†ÌÉùÎêú Ïù∏Ï¶ùÏ†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.'); setLoading(false); return; }
    const payload = {
      accessKey: cred.access_key,
      secretKey: secretKey === '********' ? '' : secretKey,
      sessionToken: sessionToken,
      awsRegion: cred.region
    };
    const res = await fetch('/aws/auth/test', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(payload)
    });
    const data = await res.json();
    if (data.success) {
      setSuccess('Ïó∞Í≤∞ ÏÑ±Í≥µ!');
      setConnectionStatus('connected');
      setIsConnected(true);
    } else {
      setError('Ïó∞Í≤∞ Ïã§Ìå®: ' + (data.message || 'Ïò§Î•ò'));
      setConnectionStatus('disconnected');
      setIsConnected(false);
    }
    setLoading(false);
  };

  // CloudWatch Î©îÌä∏Î¶≠ Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchCloudWatchMetrics = async () => {
    if (!isConnected || !awsRegion) return;

    setLoading(true);
    try {
      const params = new URLSearchParams({
        region: awsRegion,
        namespace: cloudwatchNamespace
      });

      const response = await fetch(`/aws/cloudwatch/metrics?${params}`);
      if (response.ok) {
        const data = await response.json();
        setCloudwatchMetrics(data.metrics || {});
      } else {
        console.error('CloudWatch Î©îÌä∏Î¶≠ Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®');
      }
    } catch (error) {
      console.error('CloudWatch Î©îÌä∏Î¶≠ ÏöîÏ≤≠ Ïò§Î•ò:', error);
    } finally {
      setLoading(false);
    }
  };

  // ÏûÖÎ†• ÌïÑÎìú Î≥ÄÍ≤Ω Ï≤òÎ¶¨
  const handleInputChange = (key, value) => {
    if (key === 'accessKey') setAccessKey(value);
    else if (key === 'secretKey') setSecretKey(value);
    else if (key === 'sessionToken') setSessionToken(value);
    else if (key === 'ssoStartUrl') setSsoStartUrl(value);
    else if (key === 'ssoRegion') setSsoRegion(value);
    else if (key === 'ssoAccountId') setSsoAccountId(value);
    else if (key === 'ssoRoleName') setSsoRoleName(value);
    else if (key === 'awsRegion') setAwsRegion(value);
    else if (key === 'cloudwatchNamespace') setCloudwatchNamespace(value);
  };

  // RDS Ïù∏Ïä§ÌÑ¥Ïä§ ÏÑ†ÌÉù
  const handleRdsInstanceSelect = (instanceId) => {
    handleInputChange('rds_instance_id', instanceId);
  };

  // Ïó∞Í≤∞ ÏÉÅÌÉúÏóê Îî∞Î•∏ ÏÉâÏÉÅ
  const getStatusColor = (status) => {
    switch (status) {
      case 'connected':
        return '#28a745';
      case 'connecting':
        return '#ffc107';
      case 'error':
        return '#dc3545';
      default:
        return '#6c757d';
    }
  };

  // Î©îÌä∏Î¶≠ Ïπ¥Îìú Î†åÎçîÎßÅ
  const renderMetricCard = (title, value, unit = '', status = null) => (
    <div className="metric-card">
      <div className="metric-header">
        <h4>{title}</h4>
        {status && (
          <span 
            className="status-badge"
            style={{ backgroundColor: getStatusColor(status) }}
          >
            {status}
          </span>
        )}
      </div>
      <div className="metric-value">
        <span className="value">{value}</span>
        {unit && <span className="unit">{unit}</span>}
      </div>
    </div>
  );

  return (
    <div className="aws-integration-component">
      {/* Ìó§Îçî */}
      <div className="component-header">
        <div className="header-left">
          <h2>AWS ÌÜµÌï©</h2>
          <div className="connection-status">
            <span 
              className="status-indicator"
              style={{ backgroundColor: getStatusColor(connectionStatus) }}
            ></span>
            <span className="status-text">
              {connectionStatus === 'connected' && 'Ïó∞Í≤∞Îê®'}
              {connectionStatus === 'connecting' && 'Ïó∞Í≤∞ Ï§ë...'}
              {connectionStatus === 'error' && 'Ïó∞Í≤∞ Ïò§Î•ò'}
              {connectionStatus === 'disconnected' && 'Ïó∞Í≤∞ ÏïàÎê®'}
            </span>
          </div>
        </div>
      </div>

      {/* AWS ÏÑ§Ï†ï */}
      <div className="aws-config-section">
        <h3>AWS Ïù∏Ï¶ù ÏÑ§Ï†ï</h3>

        {/* -- Ï∂îÍ∞ÄÎêú UI -- */}
        {isConnected && (
          <div className="current-config-info">
            <h4>ÌòÑÏû¨ Ïó∞Í≤∞ Ï†ïÎ≥¥</h4>
            <ul>
              <li>
                <strong>Access Key:</strong> 
                <span>{accessKey.substring(0, 4)}...{accessKey.slice(-4)}</span>
              </li>
              <li>
                <strong>AWS Region:</strong> 
                <span>{awsRegion}</span>
              </li>
            </ul>
            <p>ÏÉàÎ°úÏö¥ Ï†ïÎ≥¥Î•º ÏûÖÎ†•ÌïòÍ≥† Ï†ÄÏû•ÌïòÎ©¥ ÌòÑÏû¨ Ïó∞Í≤∞Ïù¥ Î≥ÄÍ≤ΩÎê©ÎãàÎã§.</p>
          </div>
        )}
        {/* -- Ïó¨Í∏∞ÍπåÏßÄ -- */}

        {error && (
          <div className="error-message">{error}</div>
        )}

        {success && (
          <div className="success-message">‚úÖ {success}</div>
        )}
        <div className="form-group">
          <label htmlFor="auth-type">Ïù∏Ï¶ù Î∞©Ïãù</label>
          <select
            id="auth-type"
            value={authType}
            onChange={(e) => setAuthType(e.target.value)}
            className="form-control wide-input"
          >
            {authTypes.map(type => (
              <option key={type.value} value={type.value}>{type.label}</option>
            ))}
          </select>
        </div>

        {authType === 'accessKey' && (
          <>
            <div className="form-group">
              <label htmlFor="access-key">Access Key *</label>
              <input
                type="text"
                id="access-key"
                value={accessKey}
                onChange={e => setAccessKey(e.target.value)}
                placeholder="AKIA..."
                className="form-control wide-input"
              />
            </div>
            <div className="form-group">
              <label htmlFor="secret-key">Secret Key *</label>
              <input
                type="password"
                id="secret-key"
                value={secretKey}
                onChange={e => setSecretKey(e.target.value)}
                placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                className="form-control wide-input"
              />
            </div>
            <div className="form-group">
              <label htmlFor="session-token">Session Token (ÏòµÏÖò)</label>
              <input
                type="text"
                id="session-token"
                value={sessionToken}
                onChange={e => setSessionToken(e.target.value)}
                placeholder="ÏÑ∏ÏÖò ÌÜ†ÌÅ∞ (ÌïÑÏöîÏãú)"
                className="form-control wide-input"
              />
            </div>
          </>
        )}

        {authType === 'iamRole' && (
          <div className="form-group">
            <label>IAM Role Í∏∞Î∞ò Ïù∏Ï¶ù</label>
            <div style={{ color: '#888', fontSize: '14px' }}>
              EC2/ECS/EKS Ïù∏Ïä§ÌÑ¥Ïä§Ïóê Ìï†ÎãπÎêú IAM RoleÏùÑ ÏûêÎèôÏúºÎ°ú ÏÇ¨Ïö©Ìï©ÎãàÎã§.<br />
              Î≥ÑÎèÑÏùò ÌÇ§ ÏûÖÎ†• ÏóÜÏù¥ ÏßÑÌñâÌïòÏÑ∏Ïöî.
            </div>
          </div>
        )}

        {authType === 'sso' && (
          <div className="config-form">
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="sso-start-url">SSO Start URL *</label>
                <input
                  type="text"
                  id="sso-start-url"
                  value={ssoStartUrl}
                  onChange={(e) => handleInputChange('sso-start-url', e.target.value)}
                  placeholder="https://your-sso-portal.awsapps.com/start"
                  className="form-control wide-input"
                />
              </div>
              <div className="form-group">
                <label htmlFor="sso-region">SSO Region *</label>
                <input
                  type="text"
                  id="sso-region"
                  value={ssoRegion}
                  onChange={(e) => handleInputChange('sso-region', e.target.value)}
                  placeholder="ap-northeast-2"
                  className="form-control wide-input"
                />
              </div>
            </div>
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="sso-account-id">SSO Account ID *</label>
                <input
                  type="text"
                  id="sso-account-id"
                  value={ssoAccountId}
                  onChange={(e) => handleInputChange('sso-account-id', e.target.value)}
                  placeholder="123456789012"
                  className="form-control wide-input"
                />
              </div>
              <div className="form-group">
                <label htmlFor="sso-role-name">SSO Role Name *</label>
                <input
                  type="text"
                  id="sso-role-name"
                  value={ssoRoleName}
                  onChange={(e) => handleInputChange('sso-role-name', e.target.value)}
                  placeholder="AWSAdministratorAccess"
                  className="form-control wide-input"
                />
              </div>
            </div>
          </div>
        )}

        <div className="form-row">
          <div className="form-group">
            <label htmlFor="aws-region">AWS Î¶¨Ï†Ñ</label>
            <select
              id="aws-region"
              value={awsRegion}
              onChange={(e) => setAwsRegion(e.target.value)}
              className="form-control wide-input"
            >
              {awsRegions.map(region => (
                <option key={region.value} value={region.value}>
                  {region.label}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="form-actions">
          <button 
            onClick={handleTest}
            disabled={loading || !selectedId}
            className="btn btn-outline"
          >
            {loading ? 'ÌÖåÏä§Ìä∏ Ï§ë...' : 'üîó Ïó∞Í≤∞ ÌÖåÏä§Ìä∏'}
          </button>

          <button 
            onClick={handleSave}
            disabled={loading}
            className="btn btn-primary"
          >
            {loading ? 'Ï†ÄÏû• Ï§ë...' : 'üíæ ÏÑ§Ï†ï Ï†ÄÏû•'}
          </button>
        </div>
      </div>

      {/* RDS Ïù∏Ïä§ÌÑ¥Ïä§ Í¥ÄÎ¶¨ */}
      {isConnected && (
        <div className="rds-section">
          <div className="section-header">
            <h3>RDS Ïù∏Ïä§ÌÑ¥Ïä§ Í¥ÄÎ¶¨</h3>
            <button 
              onClick={fetchRdsInstances}
              disabled={loading}
              className="btn btn-outline"
            >
              {loading ? 'Î°úÎî© Ï§ë...' : 'üîÑ Ïù∏Ïä§ÌÑ¥Ïä§ Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®'}
            </button>
          </div>

          <div className="form-group">
            <label htmlFor="rds_instance_id">RDS Ïù∏Ïä§ÌÑ¥Ïä§ ÏÑ†ÌÉù</label>
            <select
              id="rds_instance_id"
              value={selectedDb}
              onChange={(e) => handleRdsInstanceSelect(e.target.value)}
              className="form-control wide-input"
            >
              <option value="">Ïù∏Ïä§ÌÑ¥Ïä§Î•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî</option>
              {rdsInstances.length === 0 && (
                <option disabled>RDS Ïù∏Ïä§ÌÑ¥Ïä§Í∞Ä ÏóÜÏäµÎãàÎã§</option>
              )}
              {rdsInstances.map(instance => (
                <option key={instance.DBInstanceIdentifier} value={instance.DBInstanceIdentifier}>
                  {instance.DBInstanceIdentifier} ({instance.DBInstanceClass}) - {instance.DBInstanceStatus}
                </option>
              ))}
            </select>
          </div>

          {selectedDb && (
            <div className="instance-info">
              <h4>ÏÑ†ÌÉùÎêú Ïù∏Ïä§ÌÑ¥Ïä§ Ï†ïÎ≥¥</h4>
              {rdsInstances.find(i => i.DBInstanceIdentifier === selectedDb) && (
                <div className="info-grid">
                  <div className="info-item">
                    <label>Ïù∏Ïä§ÌÑ¥Ïä§ ID:</label>
                    <span>{selectedDb}</span>
                  </div>
                  <div className="info-item">
                    <label>ÏóîÏßÑ:</label>
                    <span>{rdsInstances.find(i => i.DBInstanceIdentifier === selectedDb)?.Engine}</span>
                  </div>
                  <div className="info-item">
                    <label>ÏÉÅÌÉú:</label>
                    <span>{rdsInstances.find(i => i.DBInstanceIdentifier === selectedDb)?.DBInstanceStatus}</span>
                  </div>
                  <div className="info-item">
                    <label>ÏóîÎìúÌè¨Ïù∏Ìä∏:</label>
                    <span>{rdsInstances.find(i => i.DBInstanceIdentifier === selectedDb)?.Endpoint?.Address}</span>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      )}

      {/* CloudWatch Î©îÌä∏Î¶≠ */}
      {isConnected && selectedDb && (
        <div className="cloudwatch-section">
          <div className="section-header">
            <h3>CloudWatch Î©îÌä∏Î¶≠</h3>
            <button 
              onClick={fetchCloudWatchMetrics}
              disabled={loading}
              className="btn btn-outline"
            >
              {loading ? 'Î°úÎî© Ï§ë...' : 'üìä Î©îÌä∏Î¶≠ ÏÉàÎ°úÍ≥†Ïπ®'}
            </button>
          </div>

          <div className="metrics-grid">
            {renderMetricCard(
              'CPU ÏÇ¨Ïö©Î•†',
              cloudwatchMetrics.cpu_utilization || 0,
              '%',
              cloudwatchMetrics.cpu_utilization > 80 ? 'warning' : 'ok'
            )}

            {renderMetricCard(
              'Î©îÎ™®Î¶¨ ÏÇ¨Ïö©Î•†',
              cloudwatchMetrics.freeable_memory || 0,
              'MB',
              cloudwatchMetrics.freeable_memory < 1000 ? 'warning' : 'ok'
            )}

            {renderMetricCard(
              'Ïó∞Í≤∞ Ïàò',
              cloudwatchMetrics.database_connections || 0,
              'Í∞ú',
              cloudwatchMetrics.database_connections > 100 ? 'warning' : 'ok'
            )}

            {renderMetricCard(
              'ÏùΩÍ∏∞ IOPS',
              cloudwatchMetrics.read_iops || 0,
              'IOPS',
              null
            )}

            {renderMetricCard(
              'Ïì∞Í∏∞ IOPS',
              cloudwatchMetrics.write_iops || 0,
              'IOPS',
              null
            )}

            {renderMetricCard(
              'ÎÑ§Ìä∏ÏõåÌÅ¨ Ï≤òÎ¶¨Îüâ',
              cloudwatchMetrics.network_receive_throughput || 0,
              'MB/s',
              null
            )}
          </div>

          {/* Î©îÌä∏Î¶≠ Ï∞®Ìä∏ (Í∞ÑÎã®Ìïú Ìëú ÌòïÌÉú) */}
          {Object.keys(cloudwatchMetrics).length > 0 && (
            <div className="metrics-table">
              <h4>ÏÉÅÏÑ∏ Î©îÌä∏Î¶≠</h4>
              <table className="table">
                <thead>
                  <tr>
                    <th>Î©îÌä∏Î¶≠Î™Ö</th>
                    <th>Í∞í</th>
                    <th>Îã®ÏúÑ</th>
                    <th>ÏÉÅÌÉú</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(cloudwatchMetrics).map(([key, value]) => (
                    <tr key={key}>
                      <td>{key.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}</td>
                      <td>{typeof value === 'number' ? value.toFixed(2) : value}</td>
                      <td>{getMetricUnit(key)}</td>
                      <td>
                        <span 
                          className="status-badge"
                          style={{ backgroundColor: getMetricStatusColor(key, value) }}
                        >
                          {getMetricStatus(key, value)}
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      )}

      {/* Î≥¥Ïïà Ï£ºÏùòÏÇ¨Ìï≠ */}
      <div className="security-notice">
        <h4>üîí Î≥¥Ïïà Ï£ºÏùòÏÇ¨Ìï≠</h4>
        <ul>
          <li>AWS ÏûêÍ≤© Ï¶ùÎ™ÖÏùÄ ÏïàÏ†ÑÌïòÍ≤å ÏïîÌò∏ÌôîÎêòÏñ¥ Ï†ÄÏû•Îê©ÎãàÎã§.</li>
          <li>ÏµúÏÜå Í∂åÌïú ÏõêÏπôÏóê Îî∞Îùº ÌïÑÏöîÌïú Í∂åÌïúÎßå Î∂ÄÏó¨ÌïòÏÑ∏Ïöî.</li>
          <li>Ï†ïÍ∏∞Ï†ÅÏúºÎ°ú Ïï°ÏÑ∏Ïä§ ÌÇ§Î•º ÍµêÏ≤¥ÌïòÎäî Í≤ÉÏùÑ Í∂åÏû•Ìï©ÎãàÎã§.</li>
          <li>ÌîÑÎ°úÎçïÏÖò ÌôòÍ≤ΩÏóêÏÑúÎäî IAM Ïó≠Ìï† ÏÇ¨Ïö©ÏùÑ Í≥†Î†§ÌïòÏÑ∏Ïöî.</li>
        </ul>
      </div>
    </div>
  );
}

// Î©îÌä∏Î¶≠ Îã®ÏúÑ Î∞òÌôò
const getMetricUnit = (metricName) => {
  const units = {
    cpu_utilization: '%',
    freeable_memory: 'MB',
    database_connections: 'Í∞ú',
    read_iops: 'IOPS',
    write_iops: 'IOPS',
    network_receive_throughput: 'MB/s',
    network_transmit_throughput: 'MB/s'
  };
  return units[metricName] || '';
};

// Î©îÌä∏Î¶≠ ÏÉÅÌÉú ÏÉâÏÉÅ Î∞òÌôò
const getMetricStatusColor = (metricName, value) => {
