// AwsIntegrationComponent.js
// AWS ì¸ì¦, RDS/CloudWatch ì—°ë™, ìƒíƒœ í‘œì‹œ
import React, { useState, useEffect } from 'react';

function AwsIntegrationComponent({ selectedDb, databases }) {
  const [awsConfig, setAwsConfig] = useState({
    access_key_id: '',
    secret_access_key: '',
    region: 'us-east-1',
    rds_instance_id: '',
    cloudwatch_namespace: 'AWS/RDS'
  });
  const [isConnected, setIsConnected] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('disconnected');
  const [rdsInstances, setRdsInstances] = useState([]);
  const [cloudwatchMetrics, setCloudwatchMetrics] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const awsRegions = [
    { value: 'us-east-1', label: 'US East (N. Virginia)' },
    { value: 'us-east-2', label: 'US East (Ohio)' },
    { value: 'us-west-1', label: 'US West (N. California)' },
    { value: 'us-west-2', label: 'US West (Oregon)' },
    { value: 'ap-northeast-1', label: 'Asia Pacific (Tokyo)' },
    { value: 'ap-northeast-2', label: 'Asia Pacific (Seoul)' },
    { value: 'ap-southeast-1', label: 'Asia Pacific (Singapore)' },
    { value: 'ap-southeast-2', label: 'Asia Pacific (Sydney)' },
    { value: 'eu-west-1', label: 'Europe (Ireland)' },
    { value: 'eu-central-1', label: 'Europe (Frankfurt)' }
  ];

  // AWS ì„¤ì • ë¡œë“œ
  useEffect(() => {
    loadAwsConfig();
  }, []);

  // AWS ì„¤ì • ë¡œë“œ
  const loadAwsConfig = async () => {
    try {
      const response = await fetch('/aws/config');
      if (response.ok) {
        const config = await response.json();
        setAwsConfig(config);
        setIsConnected(config.is_configured || false);
        setConnectionStatus(config.is_configured ? 'connected' : 'disconnected');
      }
    } catch (error) {
      console.error('AWS ì„¤ì • ë¡œë“œ ì˜¤ë¥˜:', error);
    }
  };

  // AWS ì—°ê²° í…ŒìŠ¤íŠ¸
  const testAwsConnection = async () => {
    setLoading(true);
    setError('');

    try {
      const response = await fetch('/aws/test-connection', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(awsConfig)
      });

      const result = await response.json();

      if (result.success) {
        setConnectionStatus('connected');
        setIsConnected(true);
        setError('');
        alert('AWS ì—°ê²° í…ŒìŠ¤íŠ¸ ì„±ê³µ!');
      } else {
        setConnectionStatus('error');
        setError(result.message || 'ì—°ê²° í…ŒìŠ¤íŠ¸ ì‹¤íŒ¨');
      }
    } catch (error) {
      setConnectionStatus('error');
      setError('ì—°ê²° í…ŒìŠ¤íŠ¸ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.');
    } finally {
      setLoading(false);
    }
  };

  // AWS ì„¤ì • ì €ì¥
  const saveAwsConfig = async () => {
    setLoading(true);
    setError('');

    try {
      const response = await fetch('/aws/config', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(awsConfig)
      });

      const result = await response.json();

      if (result.success) {
        setIsConnected(true);
        setConnectionStatus('connected');
        setError('');
        alert('AWS ì„¤ì •ì´ ì €ì¥ë˜ì—ˆìŠµë‹ˆë‹¤.');
      } else {
        setError(result.message || 'ì„¤ì • ì €ì¥ ì‹¤íŒ¨');
      }
    } catch (error) {
      setError('ì„¤ì • ì €ì¥ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.');
    } finally {
      setLoading(false);
    }
  };

  // RDS ì¸ìŠ¤í„´ìŠ¤ ëª©ë¡ ê°€ì ¸ì˜¤ê¸°
  const fetchRdsInstances = async () => {
    if (!isConnected) return;

    setLoading(true);
    try {
      const response = await fetch('/aws/rds/instances');
      if (response.ok) {
        const data = await response.json();
        setRdsInstances(data.instances || []);
      } else {
        console.error('RDS ì¸ìŠ¤í„´ìŠ¤ ëª©ë¡ ê°€ì ¸ì˜¤ê¸° ì‹¤íŒ¨');
      }
    } catch (error) {
      console.error('RDS ì¸ìŠ¤í„´ìŠ¤ ìš”ì²­ ì˜¤ë¥˜:', error);
    } finally {
      setLoading(false);
    }
  };

  // CloudWatch ë©”íŠ¸ë¦­ ê°€ì ¸ì˜¤ê¸°
  const fetchCloudWatchMetrics = async () => {
    if (!isConnected || !awsConfig.rds_instance_id) return;

    setLoading(true);
    try {
      const params = new URLSearchParams({
        instance_id: awsConfig.rds_instance_id,
        namespace: awsConfig.cloudwatch_namespace
      });

      const response = await fetch(`/aws/cloudwatch/metrics?${params}`);
      if (response.ok) {
        const data = await response.json();
        setCloudwatchMetrics(data.metrics || {});
      } else {
        console.error('CloudWatch ë©”íŠ¸ë¦­ ê°€ì ¸ì˜¤ê¸° ì‹¤íŒ¨');
      }
    } catch (error) {
      console.error('CloudWatch ë©”íŠ¸ë¦­ ìš”ì²­ ì˜¤ë¥˜:', error);
    } finally {
      setLoading(false);
    }
  };

  // ì…ë ¥ í•„ë“œ ë³€ê²½ ì²˜ë¦¬
  const handleInputChange = (key, value) => {
    setAwsConfig(prev => ({
      ...prev,
      [key]: value
    }));
  };

  // RDS ì¸ìŠ¤í„´ìŠ¤ ì„ íƒ
  const handleRdsInstanceSelect = (instanceId) => {
    handleInputChange('rds_instance_id', instanceId);
  };

  // ì—°ê²° ìƒíƒœì— ë”°ë¥¸ ìƒ‰ìƒ
  const getStatusColor = (status) => {
    switch (status) {
      case 'connected':
        return '#28a745';
      case 'connecting':
        return '#ffc107';
      case 'error':
        return '#dc3545';
      default:
        return '#6c757d';
    }
  };

  // ë©”íŠ¸ë¦­ ì¹´ë“œ ë Œë”ë§
  const renderMetricCard = (title, value, unit = '', status = null) => (
    <div className="metric-card">
      <div className="metric-header">
        <h4>{title}</h4>
        {status && (
          <span 
            className="status-badge"
            style={{ backgroundColor: getStatusColor(status) }}
          >
            {status}
          </span>
        )}
      </div>
      <div className="metric-value">
        <span className="value">{value}</span>
        {unit && <span className="unit">{unit}</span>}
      </div>
    </div>
  );

  return (
    <div className="aws-integration-component">
      {/* í—¤ë” */}
      <div className="component-header">
        <div className="header-left">
          <h2>AWS í†µí•©</h2>
          <div className="connection-status">
            <span 
              className="status-indicator"
              style={{ backgroundColor: getStatusColor(connectionStatus) }}
            ></span>
            <span className="status-text">
              {connectionStatus === 'connected' && 'ì—°ê²°ë¨'}
              {connectionStatus === 'connecting' && 'ì—°ê²° ì¤‘...'}
              {connectionStatus === 'error' && 'ì—°ê²° ì˜¤ë¥˜'}
              {connectionStatus === 'disconnected' && 'ì—°ê²° ì•ˆë¨'}
            </span>
          </div>
        </div>
      </div>

      {/* AWS ì„¤ì • */}
      <div className="aws-config-section">
        <h3>AWS ì¸ì¦ ì„¤ì •</h3>

        {error && (
          <div className="error-message">{error}</div>
        )}

        <div className="config-form">
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="access_key_id">Access Key ID *</label>
              <input
                type="text"
                id="access_key_id"
                value={awsConfig.access_key_id}
                onChange={(e) => handleInputChange('access_key_id', e.target.value)}
                placeholder="AKIA..."
                className="form-control"
              />
            </div>

            <div className="form-group">
              <label htmlFor="secret_access_key">Secret Access Key *</label>
              <input
                type="password"
                id="secret_access_key"
                value={awsConfig.secret_access_key}
                onChange={(e) => handleInputChange('secret_access_key', e.target.value)}
                placeholder="ë¹„ë°€í‚¤ ì…ë ¥"
                className="form-control"
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="region">AWS ë¦¬ì „</label>
              <select
                id="region"
                value={awsConfig.region}
                onChange={(e) => handleInputChange('region', e.target.value)}
                className="form-control"
              >
                {awsRegions.map(region => (
                  <option key={region.value} value={region.value}>
                    {region.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="cloudwatch_namespace">CloudWatch ë„¤ì„ìŠ¤í˜ì´ìŠ¤</label>
              <input
                type="text"
                id="cloudwatch_namespace"
                value={awsConfig.cloudwatch_namespace}
                onChange={(e) => handleInputChange('cloudwatch_namespace', e.target.value)}
                placeholder="AWS/RDS"
                className="form-control"
              />
            </div>
          </div>

          <div className="form-actions">
            <button 
              onClick={testAwsConnection}
              disabled={loading || !awsConfig.access_key_id || !awsConfig.secret_access_key}
              className="btn btn-outline"
            >
              {loading ? 'í…ŒìŠ¤íŠ¸ ì¤‘...' : 'ğŸ”— ì—°ê²° í…ŒìŠ¤íŠ¸'}
            </button>

            <button 
              onClick={saveAwsConfig}
              disabled={loading || !awsConfig.access_key_id || !awsConfig.secret_access_key}
              className="btn btn-primary"
            >
              {loading ? 'ì €ì¥ ì¤‘...' : 'ğŸ’¾ ì„¤ì • ì €ì¥'}
            </button>
          </div>
        </div>
      </div>

      {/* RDS ì¸ìŠ¤í„´ìŠ¤ ê´€ë¦¬ */}
      {isConnected && (
        <div className="rds-section">
          <div className="section-header">
            <h3>RDS ì¸ìŠ¤í„´ìŠ¤ ê´€ë¦¬</h3>
            <button 
              onClick={fetchRdsInstances}
              disabled={loading}
              className="btn btn-outline"
            >
              {loading ? 'ë¡œë”© ì¤‘...' : 'ğŸ”„ ì¸ìŠ¤í„´ìŠ¤ ëª©ë¡ ìƒˆë¡œê³ ì¹¨'}
            </button>
          </div>

          <div className="form-group">
            <label htmlFor="rds_instance_id">RDS ì¸ìŠ¤í„´ìŠ¤ ì„ íƒ</label>
            <select
              id="rds_instance_id"
              value={awsConfig.rds_instance_id}
              onChange={(e) => handleRdsInstanceSelect(e.target.value)}
              className="form-control"
            >
              <option value="">ì¸ìŠ¤í„´ìŠ¤ë¥¼ ì„ íƒí•˜ì„¸ìš”</option>
              {rdsInstances.map(instance => (
                <option key={instance.DBInstanceIdentifier} value={instance.DBInstanceIdentifier}>
                  {instance.DBInstanceIdentifier} ({instance.DBInstanceClass}) - {instance.DBInstanceStatus}
                </option>
              ))}
            </select>
          </div>

          {awsConfig.rds_instance_id && (
            <div className="instance-info">
              <h4>ì„ íƒëœ ì¸ìŠ¤í„´ìŠ¤ ì •ë³´</h4>
              {rdsInstances.find(i => i.DBInstanceIdentifier === awsConfig.rds_instance_id) && (
                <div className="info-grid">
                  <div className="info-item">
                    <label>ì¸ìŠ¤í„´ìŠ¤ ID:</label>
                    <span>{awsConfig.rds_instance_id}</span>
                  </div>
                  <div className="info-item">
                    <label>ì—”ì§„:</label>
                    <span>{rdsInstances.find(i => i.DBInstanceIdentifier === awsConfig.rds_instance_id)?.Engine}</span>
                  </div>
                  <div className="info-item">
                    <label>ìƒíƒœ:</label>
                    <span>{rdsInstances.find(i => i.DBInstanceIdentifier === awsConfig.rds_instance_id)?.DBInstanceStatus}</span>
                  </div>
                  <div className="info-item">
                    <label>ì—”ë“œí¬ì¸íŠ¸:</label>
                    <span>{rdsInstances.find(i => i.DBInstanceIdentifier === awsConfig.rds_instance_id)?.Endpoint?.Address}</span>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      )}

      {/* CloudWatch ë©”íŠ¸ë¦­ */}
      {isConnected && awsConfig.rds_instance_id && (
        <div className="cloudwatch-section">
          <div className="section-header">
            <h3>CloudWatch ë©”íŠ¸ë¦­</h3>
            <button 
              onClick={fetchCloudWatchMetrics}
              disabled={loading}
              className="btn btn-outline"
            >
              {loading ? 'ë¡œë”© ì¤‘...' : 'ğŸ“Š ë©”íŠ¸ë¦­ ìƒˆë¡œê³ ì¹¨'}
            </button>
          </div>

          <div className="metrics-grid">
            {renderMetricCard(
              'CPU ì‚¬ìš©ë¥ ',
              cloudwatchMetrics.cpu_utilization || 0,
              '%',
              cloudwatchMetrics.cpu_utilization > 80 ? 'warning' : 'ok'
            )}

            {renderMetricCard(
              'ë©”ëª¨ë¦¬ ì‚¬ìš©ë¥ ',
              cloudwatchMetrics.freeable_memory || 0,
              'MB',
              cloudwatchMetrics.freeable_memory < 1000 ? 'warning' : 'ok'
            )}

            {renderMetricCard(
              'ì—°ê²° ìˆ˜',
              cloudwatchMetrics.database_connections || 0,
              'ê°œ',
              cloudwatchMetrics.database_connections > 100 ? 'warning' : 'ok'
            )}

            {renderMetricCard(
              'ì½ê¸° IOPS',
              cloudwatchMetrics.read_iops || 0,
              'IOPS',
              null
            )}

            {renderMetricCard(
              'ì“°ê¸° IOPS',
              cloudwatchMetrics.write_iops || 0,
              'IOPS',
              null
            )}

