// ChatComponent.js
// ÏûêÏó∞Ïñ¥‚ÜíSQL, AI ÌÜµÌï© Ï±ÑÌåÖ Ïª¥Ìè¨ÎÑåÌä∏
import React, { useState, useEffect, useRef } from 'react';

function ChatComponent({ selectedDb, databases, onDbChange, onResetChat }) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [textareaRows, setTextareaRows] = useState(1);
  const [openaiKeys, setOpenaiKeys] = useState([]);
  const [selectedOpenAIKey, setSelectedOpenAIKey] = useState('');
  const messagesEndRef = useRef(null);
  const textareaRef = useRef(null);

  // Ï∂îÏ≤ú ÏßàÎ¨∏Îì§ (Ïπ¥ÎìúÌòï)
  const suggestedQuestions = [
    {
      title: 'ÏÑ±Îä• Î∂ÑÏÑù',
      questions: [
        'Ïò§Îäò Í∞ÄÏû• Ïò§Îûò Í±∏Î¶∞ ÏøºÎ¶¨ Î≥¥Ïó¨Ï§ò',
        'Ïä¨Î°úÏö∞ ÏøºÎ¶¨ 10Í∞úÎßå Î≥¥Ïó¨Ï§ò',
        'ÌÖåÏù¥Î∏îÎ≥Ñ row ÏàòÏôÄ ÌÅ¨Í∏∞ ÏïåÎ†§Ï§ò'
      ]
    },
    {
      title: 'Îç∞Ïù¥ÌÑ∞ ÌÉêÏÉâ',
      questions: [
        'Í∞ÄÏû• rowÍ∞Ä ÎßéÏùÄ ÌÖåÏù¥Î∏î ÏïåÎ†§Ï§ò',
        'ÏµúÍ∑º 7ÏùºÍ∞Ñ ÏÉùÏÑ±Îêú Îç∞Ïù¥ÌÑ∞ Î≥¥Ïó¨Ï§ò',
        'ÏÇ¨Ïö©Ïûê ÌÖåÏù¥Î∏îÏùò Íµ¨Ï°∞Î•º ÏÑ§Î™ÖÌï¥Ï§ò'
      ]
    },
    {
      title: 'ÏãúÏä§ÌÖú ÏÉÅÌÉú',
      questions: [
        'ÌòÑÏû¨ ÌôúÏÑ± Ïó∞Í≤∞ ÏàòÎäî?',
        'Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ ÌÅ¨Í∏∞ÏôÄ ÏÇ¨Ïö©Îüâ ÏïåÎ†§Ï§ò',
        'Ïù∏Îç±Ïä§ ÏÇ¨Ïö© ÌòÑÌô©ÏùÑ Î∂ÑÏÑùÌï¥Ï§ò'
      ]
    }
  ];

  // ÏûêÎèô Ïä§ÌÅ¨Î°§
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // OpenAI ÌÇ§ Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchOpenAIKeys = async () => {
    try {
      const response = await fetch('/api/openai/keys');
      if (response.ok) {
        const data = await response.json();
        setOpenaiKeys(data.keys || []);
        setSelectedOpenAIKey(data.selected || '');
      }
    } catch (error) {
      console.error('OpenAI ÌÇ§ Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®:', error);
    }
  };

  useEffect(() => {
    fetchOpenAIKeys();
  }, []);

  // ÌÖçÏä§Ìä∏ ÏòÅÏó≠ ÏûêÎèô ÌÅ¨Í∏∞ Ï°∞Ï†ï
  const autoResizeTextarea = (el) => {
    el.style.height = 'auto';
    const newHeight = Math.min(el.scrollHeight, 120); // ÏµúÎåÄ 5Ï§Ñ
    el.style.height = newHeight + 'px';
    setTextareaRows(Math.ceil(newHeight / 24));
  };

  // Ï∂îÏ≤ú ÏßàÎ¨∏ ÌÅ¥Î¶≠
  const handleSuggestedQuestion = (question) => {
    setInput(question);
    textareaRef.current?.focus();
  };

  // Î©îÏãúÏßÄ Ï†ÑÏÜ°
  const handleSend = async () => {
    if (!input.trim() || !selectedDb) return;

    if (!selectedOpenAIKey) {
      alert('Î®ºÏ†Ä OpenAI API ÌÇ§Î•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî. OpenAI ÏÑ§Ï†ïÏóêÏÑú ÌÇ§Î•º Îì±Î°ùÌïòÍ≥† ÏÑ†ÌÉùÌïòÏÑ∏Ïöî.');
      return;
    }

    const userMessage = { 
      role: 'user', 
      content: input,
      timestamp: new Date().toLocaleTimeString()
    };
    setMessages(prev => [...prev, userMessage]);
    setLoading(true);
    setInput('');
    setTextareaRows(1);
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
    }

    try {
      const formData = new FormData();
      formData.append('db_name', selectedDb);
      formData.append('prompt', input);

      const response = await fetch('/api/nl2sql', {
        method: 'POST',
        body: formData
      });

      if (response.ok) {
        const data = await response.json();
        if (data.error) {
          const errorMessage = {
            role: 'assistant',
            content: data.error,
            error: true,
            timestamp: new Date().toLocaleTimeString()
          };
          setMessages(prev => [...prev, errorMessage]);
        } else {
          const assistantMessage = {
            role: 'assistant',
            content: data.message.content || '',
            sql: data.message.sql || '',
            result: data.message.result || null,
            timestamp: new Date().toLocaleTimeString()
          };
          setMessages(prev => [...prev, assistantMessage]);
        }
      } else {
        throw new Error('ÏöîÏ≤≠ Ïã§Ìå®');
      }
    } catch (error) {
      const errorMessage = {
        role: 'assistant',
        content: 'Ï£ÑÏÜ°Ìï©ÎãàÎã§. ÏöîÏ≤≠ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.',
        error: true,
        timestamp: new Date().toLocaleTimeString()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setLoading(false);
    }
  };

  // ÌÇ§Î≥¥Îìú Ïù¥Î≤§Ìä∏ Ï≤òÎ¶¨
  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  // ÌÖçÏä§Ìä∏ ÏòÅÏó≠ ÏûÖÎ†• Ï≤òÎ¶¨
  const handleInputChange = (e) => {
    setInput(e.target.value);
    autoResizeTextarea(e.target);
  };

  // Ï±ÑÌåÖ Ï¥àÍ∏∞Ìôî
  const handleResetChat = async () => {
    if (!selectedDb) return;

    try {
      const formData = new FormData();
      formData.append('db_name', selectedDb);

      const response = await fetch('/api/nl2sql/reset', {
        method: 'POST',
        body: formData
      });

      if (response.ok) {
        setMessages([]);
      }
    } catch (error) {
      console.error('Ï±ÑÌåÖ Ï¥àÍ∏∞Ìôî Ïò§Î•ò:', error);
    }
  };

  return (
    <div className="chat-container">
      {/* Ï±ÑÌåÖ Ìó§Îçî */}
      <div className="chat-header">
        <div className="header-left">
          <div className="db-selector">
            <label htmlFor="db-select" className="prompt-label">DB ÏÑ†ÌÉù</label>
            <select 
              id="db-select" 
              value={selectedDb || ''} 
              onChange={(e) => onDbChange(e.target.value)}
            >
              <option value="">DBÎ•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî</option>
              {databases?.map(db => (
                <option key={db.name} value={db.name}>
                  {db.name}
                </option>
              ))}
            </select>
          </div>

          <div className="openai-status">
            <span className="status-label">OpenAI:</span>
            <span className={`status-value ${selectedOpenAIKey ? 'connected' : 'disconnected'}`}>
              {selectedOpenAIKey ? 'Ïó∞Í≤∞Îê®' : 'ÎØ∏Ïó∞Í≤∞'}
            </span>
          </div>
        </div>

        <div className="header-actions">
          <button 
            onClick={handleResetChat} 
            disabled={!selectedDb}
            className="btn btn-outline"
            title="ÎåÄÌôî Ï¥àÍ∏∞Ìôî"
          >
            üîÑ Ï¥àÍ∏∞Ìôî
          </button>
        </div>
      </div>

      {/* Ï±ÑÌåÖ Î©îÏãúÏßÄ ÏòÅÏó≠ */}
      <div className="chat-messages" id="chat-messages">
        <div className="chat-box">
          {/* Ï∂îÏ≤ú ÏßàÎ¨∏ Ïπ¥ÎìúÎì§ (Ï≤´ ÎåÄÌôî Ï†ÑÎßå ÌëúÏãú) */}
          {messages.length === 0 && (
            <div className="suggest-section">
              <h3>üí° Ï∂îÏ≤ú ÏßàÎ¨∏</h3>
              <div className="suggest-cards">
                {suggestedQuestions.map((category, categoryIndex) => (
                  <div key={categoryIndex} className="suggest-category">
                    <h4>{category.title}</h4>
                    <div className="suggest-cards-grid">
                      {category.questions.map((question, questionIndex) => (
                        <div 
                          key={questionIndex}
                          className="suggest-card" 
                          onClick={() => handleSuggestedQuestion(question)}
                        >
                          {question}
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Î©îÏãúÏßÄÎì§ */}
          {messages.map((msg, idx) => (
            <div key={idx} className={`message ${msg.role === 'user' ? 'message-user' : 'message-ai'}`}>
              <div className="message-content">
                <div className="message-header">
                  <span className="message-role">
                    {msg.role === 'user' ? 'üë§ ÏÇ¨Ïö©Ïûê' : 'ü§ñ AI'}
                  </span>
                  <span className="message-time">{msg.timestamp}</span>
                </div>

                <div className="message-body">
                  {msg.role === 'user' ? (
                    <div className="user-message">{msg.content}</div>
                  ) : (
                    <div className="ai-message">
                      {msg.content && (
                        <div className="ai-response" dangerouslySetInnerHTML={{ __html: msg.content }} />
                      )}
                      {msg.sql && (
                        <div className="sql-result">
                          <div className="sql-header">
                            <span className="sql-label">üîç ÏÉùÏÑ±Îêú SQL</span>
                          </div>
                          <div className="sql-code">
                            <pre><code>{msg.sql}</code></pre>
                          </div>
                        </div>
                      )}
                      {msg.result && (
                        <div className="query-result">
                          <div className="result-header">
                            <span className="result-label">üìä Ïã§Ìñâ Í≤∞Í≥º</span>
                          </div>
                          <div className="result-content">
                            {msg.result.headers && msg.result.data ? (
                              <div className="result-table">
                                <table>
                                  <thead>
                                    <tr>
                                      {msg.result.headers.map((header, i) => (
                                        <th key={i}>{header}</th>
                                      ))}
                                    </tr>
                                  </thead>
                                  <tbody>
                                    {msg.result.data.map((row, i) => (
                                      <tr key={i}>
                                        {row.map((cell, j) => (
                                          <td key={j}>{cell}</td>
                                        ))}
                                      </tr>
                                    ))}
                                  </tbody>
                                </table>
                              </div>
                            ) : (
                              <div className="result-text">
                                <pre><code>{JSON.stringify(msg.result, null, 2)}</code></pre>
                              </div>
                            )}
                          </div>
                        </div>
                      )}
                      {msg.error && (
                        <div className="error-message">
                          <span className="error-icon">‚ö†Ô∏è</span>
                          <span className="error-text">{msg.content}</span>
                        </div>
                      )}
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))}

          {/* Î°úÎî© ÌëúÏãú */}
          {loading && (
            <div className="message message-ai">
              <div className="message-content">
                <div className="message-header">
                  <span className="message-role">ü§ñ AI</span>
                  <span className="message-time">{new Date().toLocaleTimeString()}</span>
                </div>
                <div className="message-body">
                  <div className="loading-message">
                    <div className="loading-dots">
                      <span></span>
                      <span></span>
                      <span></span>
                    </div>
                    <span>AIÍ∞Ä ÏùëÎãµÏùÑ ÏÉùÏÑ±ÌïòÍ≥† ÏûàÏäµÎãàÎã§...</span>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
        <div ref={messagesEndRef} />
      </div>

      {/* ÏûÖÎ†• ÏòÅÏó≠ */}
      <div className="chat-input-area">
        <div className="input-container">
          <textarea
            ref={textareaRef}
            value={input}
            onChange={handleInputChange}
            onKeyDown={handleKeyDown}
            rows={textareaRows}
            placeholder="ÏûêÏó∞Ïñ¥Î°ú ÏßàÎ¨∏ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî... (Shift+EnterÎ°ú Ï§ÑÎ∞îÍøà)"
            disabled={!selectedDb || loading || !selectedOpenAIKey}
          />
          <button 
            onClick={handleSend} 
            disabled={!input.trim() || !selectedDb || loading || !selectedOpenAIKey}
            className="send-button"
          >
            <span className="send-icon">üì§</span>
          </button>
        </div>

        {!selectedOpenAIKey && (
          <div className="input-warning">
            <span>‚ö†Ô∏è OpenAI API ÌÇ§Î•º Î®ºÏ†Ä ÏÑ§Ï†ïÌï¥Ï£ºÏÑ∏Ïöî.</span>
          </div>
        )}
      </div>
    </div>
  );
}

export default ChatComponent;
