// ChatComponent.js
// ÏûêÏó∞Ïñ¥‚ÜíSQL, AI ÌÜµÌï© Ï±ÑÌåÖ Ïª¥Ìè¨ÎÑåÌä∏
import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

import ReactMarkdown from 'react-markdown';

// ÎßàÌÅ¨Îã§Ïö¥ ÌëúÎ•º HTML ÌÖåÏù¥Î∏îÎ°ú Î†åÎçîÎßÅÌïòÎäî Ìï®Ïàò
function renderMarkdownTable(md) {
  // ÎßàÌÅ¨Îã§Ïö¥ ÌëúÎßå Ï∂îÏ∂ú
  const tableMatch = md.match(/\|.*\|/g);
  if (!tableMatch) return null;
  const rows = tableMatch.map(row => row.split('|').slice(1, -1).map(cell => cell.trim()));
  if (rows.length < 2) return null;
  const headers = rows[0];
  const data = rows.slice(2); // 1: header, 2: separator, 3~: data
  return (
    <table className="result-table" style={{ minWidth: 600, fontSize: '1.05em' }}>
      <thead>
        <tr>{headers.map((h, i) => <th key={i}>{h}</th>)}</tr>
      </thead>
      <tbody>
        {data.map((row, ridx) => (
          <tr key={ridx}>{row.map((cell, cidx) => <td key={cidx}>{cell}</td>)}</tr>
        ))}
      </tbody>
    </table>
  );
}

// Î∞∞Ïó¥/Ïò§Î∏åÏ†ùÌä∏Î•º ÌëúÎ°ú Î≥ÄÌôò
function renderAutoTable(data) {
  if (Array.isArray(data)) {
    if (data.length === 0) return null;
    if (typeof data[0] === 'object' && data[0] !== null) {
      // Ïò§Î∏åÏ†ùÌä∏ Î∞∞Ïó¥
      const headers = Object.keys(data[0]);
      return (
        <table className="result-table" style={{ minWidth: 600, fontSize: '1.05em' }}>
          <thead>
            <tr>{headers.map((h, i) => <th key={i}>{h}</th>)}</tr>
          </thead>
          <tbody>
            {data.map((row, ridx) => (
              <tr key={ridx}>{headers.map((h, cidx) => <td key={cidx}>{row[h]}</td>)}</tr>
            ))}
          </tbody>
        </table>
      );
    } else {
      // Îã®Ïàú Î∞∞Ïó¥
      return (
        <table className="result-table" style={{ minWidth: 300, fontSize: '1.05em' }}>
          <tbody>
            {data.map((v, i) => <tr key={i}><td>{v}</td></tr>)}
          </tbody>
        </table>
      );
    }
  } else if (typeof data === 'object' && data !== null) {
    // Îã®Ïùº Ïò§Î∏åÏ†ùÌä∏
    const headers = Object.keys(data);
    return (
      <table className="result-table" style={{ minWidth: 300, fontSize: '1.05em' }}>
        <tbody>
          {headers.map((h, i) => <tr key={i}><th>{h}</th><td>{data[h]}</td></tr>)}
        </tbody>
      </table>
    );
  }
  return null;
}

function ChatComponent({ selectedDb, databases, onDbChange }) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [textareaRows, setTextareaRows] = useState(1);
  const messagesEndRef = useRef(null);
  const textareaRef = useRef(null);

  const [aiModels, setAiModels] = useState([]);
  const [selectedAiModel, setSelectedAiModel] = useState('');
  const [dbSchema, setDbSchema] = useState(null); // DB Ïä§ÌÇ§Îßà Ï†ïÎ≥¥

  const [conversations, setConversations] = useState([]); // ÎåÄÌôî Î™©Î°ù
  const [currentConversationId, setCurrentConversationId] = useState(null); // ÌòÑÏû¨ ÎåÄÌôî ID
  const [conversationSidebarCollapsed, setConversationSidebarCollapsed] = useState(false);

  // Ï∂îÏ≤ú ÏßàÎ¨∏Îì§ (Ïπ¥ÎìúÌòï)
  const suggestedQuestions = [
    {
      title: 'ÏÑ±Îä• Î∂ÑÏÑù',
      questions: [
        'Ïò§Îäò Í∞ÄÏû• Ïò§Îûò Í±∏Î¶∞ ÏøºÎ¶¨ Î≥¥Ïó¨Ï§ò',
        'Ïä¨Î°úÏö∞ ÏøºÎ¶¨ 10Í∞úÎßå Î≥¥Ïó¨Ï§ò',
        'ÌÖåÏù¥Î∏îÎ≥Ñ row ÏàòÏôÄ ÌÅ¨Í∏∞ ÏïåÎ†§Ï§ò'
      ]
    },
    {
      title: 'Îç∞Ïù¥ÌÑ∞ ÌÉêÏÉâ',
      questions: [
        'Í∞ÄÏû• rowÍ∞Ä ÎßéÏùÄ ÌÖåÏù¥Î∏î ÏïåÎ†§Ï§ò',
        'ÏµúÍ∑º 7ÏùºÍ∞Ñ ÏÉùÏÑ±Îêú Îç∞Ïù¥ÌÑ∞ Î≥¥Ïó¨Ï§ò',
        'ÏÇ¨Ïö©Ïûê ÌÖåÏù¥Î∏îÏùò Íµ¨Ï°∞Î•º ÏÑ§Î™ÖÌï¥Ï§ò'
      ]
    },
    {
      title: 'ÏãúÏä§ÌÖú ÏÉÅÌÉú',
      questions: [
        'ÌòÑÏû¨ ÌôúÏÑ± Ïó∞Í≤∞ ÏàòÎäî?',
        'Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ ÌÅ¨Í∏∞ÏôÄ ÏÇ¨Ïö©Îüâ ÏïåÎ†§Ï§ò',
        'Ïù∏Îç±Ïä§ ÏÇ¨Ïö© ÌòÑÌô©ÏùÑ Î∂ÑÏÑùÌï¥Ï§ò'
      ]
    }
  ];

  // ÏûêÎèô Ïä§ÌÅ¨Î°§
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // DB ÏÑ†ÌÉù Î≥ÄÍ≤Ω Ïãú ÎåÄÌôî Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    const fetchConversations = async () => {
      if (selectedDb) {
        try {
          const response = await axios.get(`/api/conversations?db_name=${selectedDb}`);
          if (response.data.status === 'success') {
            const conversations = response.data.conversations;
            setConversations(conversations);
            if (conversations.length > 0) {
              // Í∞ÄÏû• ÏµúÍ∑º ÎåÄÌôîÎ•º ÏûêÎèôÏúºÎ°ú ÏÑ†ÌÉù
              setCurrentConversationId(conversations[0].id);
            } else {
              // ÎåÄÌôîÍ∞Ä ÏóÜÏúºÎ©¥ ÌòÑÏû¨ ÎåÄÌôî Ï¥àÍ∏∞Ìôî
              setCurrentConversationId(null);
              setMessages([]);
            }
          }
        } catch (error) {
          console.error('ÎåÄÌôî Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§:', error);
          setConversations([]);
        }
      } else {
        setConversations([]);
        setCurrentConversationId(null);
        setMessages([]);
      }
    };

    fetchConversations();
  }, [selectedDb]);

  // DB ÏÑ†ÌÉù Î≥ÄÍ≤Ω Ïãú Ïä§ÌÇ§Îßà Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    const fetchDbSchema = async () => {
      if (selectedDb && selectedDb !== '__ALL_DBS__') {
        try {
          const response = await axios.get(`/api/schema/${selectedDb}`);
          if (response.data.status === 'success') {
            setDbSchema(response.data.schema);
            console.log('DB Ïä§ÌÇ§Îßà Ï†ïÎ≥¥ Î°úÎìú ÏôÑÎ£å:', response.data.schema);
          }
        } catch (error) {
          console.error('DB Ïä§ÌÇ§Îßà Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§:', error);
          setDbSchema(null);
        }
      } else {
        setDbSchema(null);
      }
    };

    fetchDbSchema();
  }, [selectedDb]);

  // ÌòÑÏû¨ ÎåÄÌôî ID Î≥ÄÍ≤Ω Ïãú Î©îÏãúÏßÄ Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    const fetchMessages = async () => {
      if (currentConversationId) {
        try {
          const response = await axios.get(`/api/conversations/${currentConversationId}/messages`);
          if (response.data.status === 'success') {
            // DBÏóêÏÑú Î∂àÎü¨Ïò® Î©îÏãúÏßÄ ÌòïÏãù Î≥ÄÌôò
            const formattedMessages = response.data.messages.map(msg => ({
              role: msg.role,
              content: msg.content,
              sql: msg.sql_query,
              result: msg.sql_result ? JSON.parse(msg.sql_result) : null,
              timestamp: new Date(msg.timestamp).toLocaleTimeString()
            }));
            setMessages(formattedMessages);
          }
        } catch (error) {
          console.error('Î©îÏãúÏßÄÎ•º Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§:', error);
          setMessages([]);
        }
      } else {
        setMessages([]);
      }
    };
    fetchMessages();
  }, [currentConversationId]);

  // AI Î™®Îç∏ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ (Ìïú Î≤àÎßå Ïã§Ìñâ)
  useEffect(() => {
    const fetchAiModels = async () => {
      try {
        const openaiRes = await axios.get('/api/openai/keys');
        const azureOpenAIRes = await axios.get('/api/azure-openai/configs');
        const geminiRes = await axios.get('/api/gemini/configs');
        const claudeRes = await axios.get('/api/claude/configs');

        const allModels = [
          ...openaiRes.data.keys.map(k => ({ ...k, type: 'openai' })),
          ...azureOpenAIRes.data.configs.map(c => ({ ...c, type: 'azure_openai' })),
          ...geminiRes.data.configs.map(c => ({ ...c, type: 'gemini' })),
          ...claudeRes.data.configs.map(c => ({ ...c, type: 'claude' })),
        ];
        setAiModels(allModels);

        const currentSelected = allModels.find(m => m.is_selected);
        if (currentSelected) {
          setSelectedAiModel(currentSelected.name);
        } else if (allModels.length > 0) {
          const defaultModel = allModels[0];
          let selectApiEndpoint = '';
          if (defaultModel.type === 'openai') selectApiEndpoint = '/api/openai/select';
          else if (defaultModel.type === 'azure_openai') selectApiEndpoint = '/api/azure-openai/select';
          else if (defaultModel.type === 'gemini') selectApiEndpoint = '/api/gemini/select';
          else if (defaultModel.type === 'claude') selectApiEndpoint = '/api/claude/select';

          if (selectApiEndpoint) {
            const formData = new FormData();
            formData.append('name', defaultModel.name);
            axios.post(selectApiEndpoint, formData)
              .then(() => {
                setSelectedAiModel(defaultModel.name);
                console.log(`Default AI model selected: ${defaultModel.name}`);
              })
              .catch(error => {
                console.error('Error selecting default AI model:', error);
              });
          }
        }

      } catch (error) {
        console.error('Error fetching AI models:', error);
      }
    };
    fetchAiModels();
  }, []);

  // ÌÖçÏä§Ìä∏ ÏòÅÏó≠ ÏûêÎèô ÌÅ¨Í∏∞ Ï°∞Ï†ï
  const autoResizeTextarea = (el) => {
    el.style.height = 'auto';
    const newHeight = Math.min(el.scrollHeight, 120); // ÏµúÎåÄ 5Ï§Ñ
    el.style.height = newHeight + 'px';
    setTextareaRows(Math.ceil(newHeight / 24));
  };

  // Ï∂îÏ≤ú ÏßàÎ¨∏ ÌÅ¥Î¶≠
  const handleSuggestedQuestion = (question) => {
    setInput(question);
    textareaRef.current?.focus();
  };

  // DB Ïä§ÌÇ§Îßà Ï†ïÎ≥¥Î•º AI ÌîÑÎ°¨ÌîÑÌä∏Î°ú Î≥ÄÌôò
  const generateSchemaPrompt = (schema) => {
    let prompt = '';

    // ÌÖåÏù¥Î∏î Ï†ïÎ≥¥
    prompt += `Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§Ïóê ${schema.tables.length}Í∞úÏùò ÌÖåÏù¥Î∏îÏù¥ ÏûàÏäµÎãàÎã§:\n\n`;

    schema.tables.forEach(table => {
      prompt += `ÌÖåÏù¥Î∏î: ${table.name}`;
      if (table.comment) {
        prompt += ` (${table.comment})`;
      }
      prompt += '\n';

      // Ïª¨Îüº Ï†ïÎ≥¥
      prompt += '  Ïª¨Îüº:\n';
      table.columns.forEach(column => {
        prompt += `    - ${column.name}: ${column.type}`;
        if (column.max_length) {
          prompt += `(${column.max_length})`;
        }
        if (column.precision && column.scale) {
          prompt += `(${column.precision},${column.scale})`;
        }
        if (!column.nullable) {
          prompt += ' (NOT NULL)';
        }
        if (column.default) {
          prompt += ` (Í∏∞Î≥∏Í∞í: ${column.default})`;
        }
        if (column.comment) {
          prompt += ` - ${column.comment}`;
        }
        prompt += '\n';
      });

      // Ïù∏Îç±Ïä§ Ï†ïÎ≥¥
      if (table.indexes && table.indexes.length > 0) {
        prompt += '  Ïù∏Îç±Ïä§:\n';
        table.indexes.forEach(index => {
          prompt += `    - ${index.name}: [${index.columns.join(', ')}]`;
          if (index.unique) {
            prompt += ' (UNIQUE)';
          }
          if (index.primary) {
            prompt += ' (PRIMARY KEY)';
          }
          prompt += '\n';
        });
      }
      prompt += '\n';
    });

    // Í¥ÄÍ≥Ñ Ï†ïÎ≥¥
    if (schema.relationships && schema.relationships.length > 0) {
      prompt += 'ÌÖåÏù¥Î∏î Í¥ÄÍ≥Ñ:\n';
      schema.relationships.forEach(rel => {
        prompt += `  ${rel.table}.${rel.column} -> ${rel.foreign_table}.${rel.foreign_column}\n`;
      });
      prompt += '\n';
    }

    return prompt;
  };

  // Í∏∞Ï°¥ ÎåÄÌôî ÏÑ†ÌÉù
  const handleSelectConversation = (convId) => {
    setCurrentConversationId(convId);
  };

  // ÎåÄÌôî ÏÇ≠Ï†ú
  const handleDeleteConversation = async (convId) => {
    if (window.confirm('Ï†ïÎßêÎ°ú Ïù¥ ÎåÄÌôîÎ•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå? Î™®Îì† Î©îÏãúÏßÄÍ∞Ä ÏòÅÍµ¨Ï†ÅÏúºÎ°ú ÏÇ≠Ï†úÎê©ÎãàÎã§.')) {
      try {
        await axios.delete(`/api/conversations/${convId}`);
        // ÎåÄÌôî Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
        const convRes = await axios.get(`/api/conversations?db_name=${selectedDb}`);
        if (convRes.data.status === 'success') {
          setConversations(convRes.data.conversations);
        }
        if (currentConversationId === convId) {
          setCurrentConversationId(null);
          setMessages([]);
        }
      } catch (error) {
        console.error('ÎåÄÌôî ÏÇ≠Ï†ú Ïã§Ìå®:', error);
        alert('ÎåÄÌôî ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    }
  };

  const splitQueries = (input) => {
    // ÏÑ∏ÎØ∏ÏΩúÎ°†(;) Í∏∞Ï§ÄÏúºÎ°ú ÏøºÎ¶¨ Î∂ÑÎ¶¨, Ï£ºÏÑù/ÎπàÏπ∏ Î¨¥Ïãú
    return input
      .split(';')
      .map(q => q.trim())
      .filter(q => q && !q.startsWith('--'));
  };

  // Î©îÏãúÏßÄ Ï†ÑÏÜ°
  const handleSend = async () => {
    if (!input.trim()) return;
    setLoading(true);
    const queries = splitQueries(input);
    let first = true;
    for (const query of queries) {
      try {
        const formData = new FormData();
        formData.append('db_name', selectedDb);
        formData.append('prompt', query);
        formData.append('conversation_id', currentConversationId || '');
        const response = await axios.post('/api/nl2sql', formData);
        if (response.data.status === 'success') {
          const data = response.data.message;
          const assistantMessage = {
            role: 'assistant',
            content: data.content || '',
            sql: data.sql || '',
            result: data.result || null,
            timestamp: new Date().toLocaleTimeString()
          };
          setMessages(prev => first ? [...prev, assistantMessage] : [...prev, { ...assistantMessage, content: `(ÏøºÎ¶¨ ${queries.indexOf(query)+1})` }]);
        } else {
          const errorMessage = {
            role: 'assistant',
            content: response.data.error || 'Ïïå Ïàò ÏóÜÎäî Ïò§Î•ò Î∞úÏÉù',
            error: true,
            timestamp: new Date().toLocaleTimeString()
          };
          setMessages(prev => [...prev, errorMessage]);
        }
      } catch (error) {
        const errorMessage = {
          role: 'assistant',
          content: 'Ï£ÑÏÜ°Ìï©ÎãàÎã§. ÏöîÏ≤≠ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.',
          error: true,
          timestamp: new Date().toLocaleTimeString()
        };
        setMessages(prev => [...prev, errorMessage]);
      }
      first = false;
    }
    setLoading(false);
    setInput('');
  };

  // ÌÇ§Î≥¥Îìú Ïù¥Î≤§Ìä∏ Ï≤òÎ¶¨
  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  // ÌÖçÏä§Ìä∏ ÏòÅÏó≠ ÏûÖÎ†• Ï≤òÎ¶¨
  const handleInputChange = (e) => {
    setInput(e.target.value);
    autoResizeTextarea(e.target);
  };

  // Ï±ÑÌåÖ Ï¥àÍ∏∞Ìôî (ÌòÑÏû¨ ÎåÄÌôîÏùò Î©îÏãúÏßÄÎßå ÏÇ≠Ï†ú)
  const handleResetChat = async () => {
    if (!currentConversationId) return;

    if (window.confirm('ÌòÑÏû¨ ÎåÄÌôîÏùò Î™®Îì† Î©îÏãúÏßÄÎ•º Ï¥àÍ∏∞ÌôîÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      try {
        const formData = new FormData();
        formData.append('conversation_id', currentConversationId);
        const response = await axios.post('/api/nl2sql/reset', formData);

        if (response.data.status === 'success') {
          setMessages([]);
          // ÎåÄÌôî Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ® (ÏóÖÎç∞Ïù¥Ìä∏Îêú updated_at Î∞òÏòÅ)
          const convRes = await axios.get(`/api/conversations?db_name=${selectedDb}`);
          if (convRes.data.status === 'success') {
            setConversations(convRes.data.conversations);
          }
        }
      } catch (error) {
        console.error('Ï±ÑÌåÖ Ï¥àÍ∏∞Ìôî Ïò§Î•ò:', error);
        alert('Ï±ÑÌåÖ Ï¥àÍ∏∞ÌôîÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    }
  };

  return (
    <div className="chat-page-container">
      {/* ÎåÄÌôî Î™©Î°ù ÏÇ¨Ïù¥ÎìúÎ∞î */}
      <div className={`conversation-sidebar${conversationSidebarCollapsed ? ' collapsed' : ''}`}> 
        <div className="sidebar-header">
          <h3>üí¨ ÎåÄÌôî Î™©Î°ù</h3>
          <button 
            className="btn btn-primary btn-sm"
            onClick={() => {
              setCurrentConversationId(null);
              setMessages([]);
            }}
            title="ÏÉà ÎåÄÌôî ÏãúÏûë"
          >
            ‚ûï ÏÉà ÎåÄÌôî
          </button>
          <button 
            className="sidebar-toggle-btn"
            style={{ marginLeft: 8 }}
            onClick={() => setConversationSidebarCollapsed(v => !v)}
            title={conversationSidebarCollapsed ? 'ÎåÄÌôîÎ™©Î°ù ÌéºÏπòÍ∏∞' : 'ÎåÄÌôîÎ™©Î°ù Ï†ëÍ∏∞'}
          >
            {conversationSidebarCollapsed ? '‚ñ∂' : '‚óÄ'}
          </button>
        </div>
        {!conversationSidebarCollapsed && (
          <div className="conversation-list-container">
            {conversations.length === 0 ? (
              <div className="no-conversations">
                <p>ÏïÑÏßÅ ÎåÄÌôîÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
                <p>ÏÉà ÎåÄÌôîÎ•º ÏãúÏûëÌï¥Î≥¥ÏÑ∏Ïöî!</p>
              </div>
            ) : (
              <ul className="conversation-list list-group">
                {conversations.map(conv => (
                  <li 
                    key={conv.id}
                    className={`conversation-item list-group-item${currentConversationId === conv.id ? ' active' : ''}`}
                    onClick={() => handleSelectConversation(conv.id)}
                  >
                    <div className="conversation-content">
                      <div className="conversation-title">{conv.title}</div>
                      <div className="conversation-meta">{conv.updated_at}</div>
                    </div>
                    <button className="delete-btn btn btn-danger btn-sm" onClick={e => { e.stopPropagation(); handleDeleteConversation(conv.id); }}>ÏÇ≠Ï†ú</button>
                  </li>
                ))}
              </ul>
            )}
          </div>
        )}
      </div>

      <div className="chat-container">
        {/* Ï±ÑÌåÖ Ìó§Îçî */}
        <div className="chat-header">
          <div className="header-left">
            <div className="db-selector">
              <label htmlFor="db-select" className="prompt-label">DB ÏÑ†ÌÉù</label>
              <select 
                id="db-select" 
                value={selectedDb || ''} 
                onChange={(e) => onDbChange(e.target.value)}
              >
                <option value="">DBÎ•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî</option>
                <option value="__ALL_DBS__">Î™®Îì† DB</option>
                {databases?.map(db => (
                  <option key={db.name} value={db.name}>
                    {db.name}
                  </option>
                ))}
              </select>
            </div>

            {/* AI Î™®Îç∏ ÏÑ†ÌÉù ÎìúÎ°≠Îã§Ïö¥ */}
            <div className="ai-model-selector ms-3">
              <label htmlFor="ai-model-select" className="prompt-label">AI Î™®Îç∏ ÏÑ†ÌÉù</label>
              <select 
                id="ai-model-select" 
                value={selectedAiModel} 
                onChange={async (e) => {
                  const modelName = e.target.value;
                  const selected = aiModels.find(m => m.name === modelName);
                  if (selected) {
                    let selectApiEndpoint = '';
                    if (selected.type === 'openai') selectApiEndpoint = '/api/openai/select';
                    else if (selected.type === 'azure_openai') selectApiEndpoint = '/api/azure-openai/select';
                    else if (selected.type === 'gemini') selectApiEndpoint = '/api/gemini/select';
                    else if (selected.type === 'claude') selectApiEndpoint = '/api/claude/select';

                    if (selectApiEndpoint) {
                      try {
                        const formData = new FormData();
                        formData.append('name', selected.name);
                        await axios.post(selectApiEndpoint, formData);
                        setSelectedAiModel(selected.name);
                        alert(`${selected.name} AI Î™®Îç∏Ïù¥ ÏÑ†ÌÉùÎêòÏóàÏäµÎãàÎã§.`);
                      } catch (error) {
                        console.error('Error selecting AI model:', error);
                        alert('AI Î™®Îç∏ ÏÑ†ÌÉùÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
                      }
                    }
                  }
                }}
              >
                <option value="">AI Î™®Îç∏ ÏÑ†ÌÉù</option>
                {aiModels.map(model => (
                  <option key={model.name} value={model.name}>
                    {model.name} ({model.type})
                  </option>
                ))}
              </select>
            </div>

          </div>

          <div className="header-actions">
            {currentConversationId && (
              <button 
                onClick={handleResetChat} 
                className="btn btn-outline-warning btn-sm"
                title="ÌòÑÏû¨ ÎåÄÌôî Ï¥àÍ∏∞Ìôî"
              >
                üîÑ Ï¥àÍ∏∞Ìôî
              </button>
            )}
            <button 
              onClick={() => {
                setCurrentConversationId(null);
                setMessages([]);
              }}
              className="btn btn-outline-primary btn-sm ms-2"
              title="ÏÉà ÎåÄÌôî ÏãúÏûë"
            >
              ‚ûï ÏÉà ÎåÄÌôî
            </button>
          </div>
        </div>

        {/* Ï±ÑÌåÖ Î©îÏãúÏßÄ ÏòÅÏó≠ */}
        <div className="chat-messages" id="chat-messages">
          <div className="chat-box">
            {/* Ï∂îÏ≤ú ÏßàÎ¨∏ Ïπ¥ÎìúÎì§ (Ï≤´ ÎåÄÌôî Ï†ÑÎßå ÌëúÏãú) */}
            {messages.length === 0 && !currentConversationId && (
              <div className="suggest-section">
                <h3>üí° Ï∂îÏ≤ú ÏßàÎ¨∏</h3>
                <div className="suggest-cards">
                  {suggestedQuestions.map((category, categoryIndex) => (
                    <div key={categoryIndex} className="suggest-category">
                      <h4>{category.title}</h4>
                      <div className="suggest-cards-grid">
                        {category.questions.map((question, questionIndex) => (
                          <div 
                            key={questionIndex}
                            className="suggest-card" 
                            onClick={() => handleSuggestedQuestion(question)}
                          >
                            {question}
                          </div>
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Î©îÏãúÏßÄÎì§ */}
            {messages.map((msg, idx) => (
              <div key={idx} className={`message ${msg.role === 'user' ? 'message-user' : 'message-ai'}`}>
                <div className="message-content">
                  <div className="message-header">
                    <span className="message-role">
                      {msg.role === 'user' ? 'üë§ ÏÇ¨Ïö©Ïûê' : 'ü§ñ AI'}
                    </span>
                    <span className="message-time">{msg.timestamp}</span>
                  </div>

                  <div className="message-body">
                    {msg.role === 'user' ? (
                      <div className="user-message">{msg.content}</div>
                    ) : (
                      <div className="ai-message-card">
                        {msg.content && (
                          <div className="ai-response-section">
                            <ReactMarkdown>{msg.content}</ReactMarkdown>
                          </div>
                        )}
                        {msg.sql && (
                          <div className="sql-section">
                            <div className="section-header">
                              <strong>üîç ÏÉùÏÑ±Îêú SQL</strong>
                              <button onClick={() => navigator.clipboard.writeText(msg.sql)} className="btn-copy">Î≥µÏÇ¨</button>
                            </div>
                            <pre><code>{msg.sql}</code></pre>
                          </div>
                        )}
                        {msg.result && (
                          <div className="result-section">
                            <div className="section-header">
                              <strong>üìä ÏøºÎ¶¨ Í≤∞Í≥º</strong>
                            </div>
                            <div className="result-table-container" style={{ maxWidth: '100%', overflowX: 'auto', minWidth: 600 }}>
                              {/* headers/data Íµ¨Ï°∞Î©¥ ÌëúÎ°ú, ÏïÑÎãàÎ©¥ Í∏∞Ï°¥ Î∞©Ïãù */}
                              {msg.result.headers && msg.result.data ? (
                                msg.result.data.length > 0 ? (
                                  <table className="result-table" style={{ minWidth: 600, fontSize: '1.05em' }}>
                                    <thead>
                                      <tr>
                                        {msg.result.headers.map((header, idx) => (
                                          <th key={idx} style={{ padding: '8px 12px', background: '#f5f5f5', border: '1px solid #ddd' }}>{header}</th>
                                        ))}
                                      </tr>
                                    </thead>
                                    <tbody>
                                      {msg.result.data.map((row, ridx) => (
                                        <tr key={ridx}>
                                          {row.map((cell, cidx) => (
                                            <td key={cidx} style={{ padding: '8px 12px', border: '1px solid #eee', background: ridx % 2 === 0 ? '#fff' : '#fafbfc' }}>{cell === null ? '' : String(cell)}</td>
                                          ))}
                                        </tr>
                                      ))}
                                    </tbody>
                                  </table>
                                ) : (
                                  <div style={{ color: '#888', padding: '16px' }}>Îç∞Ïù¥ÌÑ∞ ÏóÜÏùå</div>
                                )
                              ) : (
                                // ÎßàÌÅ¨Îã§Ïö¥ Ìëú ÏûêÎèô Î≥ÄÌôò
                                (typeof msg.result === 'string' && renderMarkdownTable(msg.result)) ||
                                // Î∞∞Ïó¥/Ïò§Î∏åÏ†ùÌä∏ ÏûêÎèô Î≥ÄÌôò
                                renderAutoTable(msg.result) ||
                                // Í∏∞Ï°¥ Î∞©Ïãù fallback
                                <pre style={{ fontSize: '1.05em', background: '#f8f8f8', padding: 12, borderRadius: 6, overflowX: 'auto' }}>{typeof msg.result === 'object' ? JSON.stringify(msg.result, null, 2) : String(msg.result)}</pre>
                              )}
                            </div>
                          </div>
                        )}
                        {msg.error && (
                          <div className="error-section">
                            <div className="alert alert-danger">
                              <strong>‚ùå Ïò§Î•ò Î∞úÏÉù</strong>
                              <p>{msg.content}</p>
                            </div>
                          </div>
                        )}
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ))}

            {/* Î°úÎî© Ïù∏ÎîîÏºÄÏù¥ÌÑ∞ */}
            {loading && (
              <div className="message message-ai">
                <div className="message-content">
                  <div className="message-header">
                    <span className="message-role">ü§ñ AI</span>
                    <span className="message-time">Ï≤òÎ¶¨ Ï§ë...</span>
                  </div>
                  <div className="message-body">
                    <div className="loading-indicator">
                      <div className="spinner"></div>
                      <span>AIÍ∞Ä ÏùëÎãµÏùÑ ÏÉùÏÑ±ÌïòÍ≥† ÏûàÏäµÎãàÎã§...</span>
                    </div>
                  </div>
                </div>
              </div>
            )}

            <div ref={messagesEndRef} />
          </div>
        </div>

        {/* ÏûÖÎ†• ÏòÅÏó≠ */}
        <div className="chat-input-area">
          <div className="input-container">
            <textarea
              ref={textareaRef}
              value={input}
              onChange={handleInputChange}
              onKeyDown={handleKeyDown}
              placeholder="ÏßàÎ¨∏ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî... (Shift+EnterÎ°ú Ï§ÑÎ∞îÍøà)"
              className="chat-input"
              rows={textareaRows}
              disabled={loading || !selectedDb}
            />
            <button
              onClick={handleSend}
              disabled={loading || !input.trim() || !selectedDb}
              className="send-button"
            >
              {loading ? '‚è≥' : 'üì§'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ChatComponent;
