// ChatComponent.js
// 자연어→SQL, AI 통합 채팅 컴포넌트
import React, { useState, useEffect, useRef } from 'react';

function ChatComponent({ selectedDb, databases, onDbChange, onResetChat, openaiKeys, selectedOpenAIKey }) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [textareaRows, setTextareaRows] = useState(1);
  const messagesEndRef = useRef(null);
  const textareaRef = useRef(null);

  // 추천 질문들 (카드형)
  const suggestedQuestions = [
    {
      title: '성능 분석',
      questions: [
        '오늘 가장 오래 걸린 쿼리 보여줘',
        '슬로우 쿼리 10개만 보여줘',
        '테이블별 row 수와 크기 알려줘'
      ]
    },
    {
      title: '데이터 탐색',
      questions: [
        '가장 row가 많은 테이블 알려줘',
        '최근 7일간 생성된 데이터 보여줘',
        '사용자 테이블의 구조를 설명해줘'
      ]
    },
    {
      title: '시스템 상태',
      questions: [
        '현재 활성 연결 수는?',
        '데이터베이스 크기와 사용량 알려줘',
        '인덱스 사용 현황을 분석해줘'
      ]
    }
  ];

  // 자동 스크롤
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);



  // DB 선택 변경 시 채팅 기록 불러오기
  useEffect(() => {
    const fetchChatHistory = async () => {
      if (selectedDb) {
        try {
          const response = await fetch(`/api/nl2sql/history?db_name=${selectedDb}`);
          if (response.ok) {
            const data = await response.json();
            setMessages(data.history || []);
          } else {
            console.error('채팅 기록을 불러오는데 실패했습니다.');
            setMessages([]);
          }
        } catch (error) {
          console.error('채팅 기록 불러오기 오류:', error);
          setMessages([]);
        }
      } else {
        setMessages([]); // DB가 선택되지 않으면 메시지 초기화
      }
    };
    fetchChatHistory();
  }, [selectedDb]);

  // 텍스트 영역 자동 크기 조정
  const autoResizeTextarea = (el) => {
    el.style.height = 'auto';
    const newHeight = Math.min(el.scrollHeight, 120); // 최대 5줄
    el.style.height = newHeight + 'px';
    setTextareaRows(Math.ceil(newHeight / 24));
  };

  // 추천 질문 클릭
  const handleSuggestedQuestion = (question) => {
    setInput(question);
    textareaRef.current?.focus();
  };

  // 메시지 전송
  const handleSend = async () => {
    if (!input.trim() || !selectedDb) return;

    if (!selectedOpenAIKey) {
      alert('먼저 OpenAI API 키를 선택해주세요. OpenAI 설정에서 키를 등록하고 선택하세요.');
      return;
    }

    const userMessage = { 
      role: 'user', 
      content: input,
      timestamp: new Date().toLocaleTimeString()
    };
    setMessages(prev => [...prev, userMessage]);
    setLoading(true);
    setInput('');
    setTextareaRows(1);
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
    }

    try {
      const formData = new FormData();
      formData.append('db_name', selectedDb);
      formData.append('prompt', input);

      const response = await fetch('/api/nl2sql', {
        method: 'POST',
        body: formData
      });

      if (response.ok) {
        const data = await response.json();
        if (data.error) {
          const errorMessage = {
            role: 'assistant',
            content: data.error,
            error: true,
            timestamp: new Date().toLocaleTimeString()
          };
          setMessages(prev => [...prev, errorMessage]);
        } else {
          const assistantMessage = {
            role: 'assistant',
            content: data.message.content || '',
            sql: data.message.sql || '',
            result: data.message.result || null,
            timestamp: new Date().toLocaleTimeString()
          };
          setMessages(prev => [...prev, assistantMessage]);
        }
      } else {
        throw new Error('요청 실패');
      }
    } catch (error) {
      const errorMessage = {
        role: 'assistant',
        content: '죄송합니다. 요청 처리 중 오류가 발생했습니다.',
        error: true,
        timestamp: new Date().toLocaleTimeString()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setLoading(false);
    }
  };

  // 키보드 이벤트 처리
  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  // 텍스트 영역 입력 처리
  const handleInputChange = (e) => {
    setInput(e.target.value);
    autoResizeTextarea(e.target);
  };

  // 채팅 초기화
  const handleResetChat = async () => {
    if (!selectedDb) return;

    try {
      const formData = new FormData();
      formData.append('db_name', selectedDb);

      const response = await fetch('/api/nl2sql/reset', {
        method: 'POST',
        body: formData
      });

      if (response.ok) {
        setMessages([]);
      }
    } catch (error) {
      console.error('채팅 초기화 오류:', error);
    }
  };

  return (
    <div className="chat-container">
      {/* 채팅 헤더 */}
      <div className="chat-header">
        <div className="header-left">
          <div className="db-selector">
            <label htmlFor="db-select" className="prompt-label">DB 선택</label>
            <select 
              id="db-select" 
              value={selectedDb || ''} 
              onChange={(e) => onDbChange(e.target.value)}
            >
              <option value="">DB를 선택하세요</option>
              <option value="__ALL_DBS__">모든 DB</option>
              {databases?.map(db => (
                <option key={db.name} value={db.name}>
                  {db.name}
                </option>
              ))}
            </select>
          </div>

          <div className="openai-status">
            <span className="status-label">OpenAI:</span>
            <span className={`status-value ${selectedOpenAIKey ? 'connected' : 'disconnected'}`}>
              {selectedOpenAIKey ? '연결됨' : '미연결'}
            </span>
          </div>
        </div>

        <div className="header-actions">
          <button 
            onClick={handleResetChat} 
            disabled={!selectedDb}
            className="btn btn-outline"
            title="대화 초기화"
          >
            🔄 초기화
          </button>
        </div>
      </div>

      {/* 채팅 메시지 영역 */}
      <div className="chat-messages" id="chat-messages">
        <div className="chat-box">
          {/* 추천 질문 카드들 (첫 대화 전만 표시) */}
          {messages.length === 0 && (
            <div className="suggest-section">
              <h3>💡 추천 질문</h3>
              <div className="suggest-cards">
                {suggestedQuestions.map((category, categoryIndex) => (
                  <div key={categoryIndex} className="suggest-category">
                    <h4>{category.title}</h4>
                    <div className="suggest-cards-grid">
                      {category.questions.map((question, questionIndex) => (
                        <div 
                          key={questionIndex}
                          className="suggest-card" 
                          onClick={() => handleSuggestedQuestion(question)}
                        >
                          {question}
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* 메시지들 */}
          {messages.map((msg, idx) => (
            <div key={idx} className={`message ${msg.role === 'user' ? 'message-user' : 'message-ai'}`}>
              <div className="message-content">
                <div className="message-header">
                  <span className="message-role">
                    {msg.role === 'user' ? '👤 사용자' : '🤖 AI'}
                  </span>
                  <span className="message-time">{msg.timestamp}</span>
                </div>

                <div className="message-body">
                  {msg.role === 'user' ? (
                    <div className="user-message">{msg.content}</div>
                  ) : (
                    <div className="ai-message">
                      {msg.content && (
                        <div className="ai-response" dangerouslySetInnerHTML={{ __html: msg.content }} />
                      )}
                      {msg.sql && (
                        <div className="sql-result">
                          <div className="sql-header">
                            <span className="sql-label">🔍 생성된 SQL</span>
                          </div>
                          <div className="sql-code">
                            <pre><code>{msg.sql}</code></pre>
                          </div>
                        </div>
                      )}
                      {msg.result && (
                        <div className="query-result">
                          <div className="result-header">
                            <span className="result-label">📊 실행 결과</span>
                          </div>
                          <div className="result-content">
                            {msg.result.headers && msg.result.data ? (
                              <div className="result-table">
                                <table>
                                  <thead>
                                    <tr>
                                      {msg.result.headers.map((header, i) => (
                                        <th key={i}>{header}</th>
                                      ))}
                                    </tr>
                                  </thead>
                                  <tbody>
                                    {msg.result.data.map((row, i) => (
                                      <tr key={i}>
                                        {row.map((cell, j) => (
                                          <td key={j}>{cell}</td>
                                        ))}
                                      </tr>
                                    ))}
                                  </tbody>
                                </table>
                              </div>
                            ) : (
                              <div className="result-text">
                                <pre><code>{JSON.stringify(msg.result, null, 2)}</code></pre>
                              </div>
                            )}
                          </div>
                        </div>
                      )}
                      {msg.error && (
                        <div className="error-message">
                          <span className="error-icon">⚠️</span>
                          <span className="error-text">{msg.content}</span>
                        </div>
                      )}
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))}

          {/* 로딩 표시 */}
          {loading && (
            <div className="message message-ai">
              <div className="message-content">
                <div className="message-header">
                  <span className="message-role">🤖 AI</span>
                  <span className="message-time">{new Date().toLocaleTimeString()}</span>
                </div>
                <div className="message-body">
                  <div className="loading-message">
                    <div className="loading-dots">
                      <span></span>
                      <span></span>
                      <span></span>
                    </div>
                    <span>AI가 응답을 생성하고 있습니다...</span>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
        <div ref={messagesEndRef} />
      </div>

      {/* 입력 영역 */}
      <div className="chat-input-area">
        <div className="input-container">
          <textarea
            ref={textareaRef}
            value={input}
            onChange={handleInputChange}
            onKeyDown={handleKeyDown}
            rows={textareaRows}
            placeholder="자연어로 질문을 입력하세요... (Shift+Enter로 줄바꿈)"
            disabled={!selectedDb || loading || !selectedOpenAIKey}
          />
          <button 
            onClick={handleSend} 
            disabled={!input.trim() || !selectedDb || loading || !selectedOpenAIKey}
            className="send-button"
          >
            <span className="send-icon">📤</span>
          </button>
        </div>

        {!selectedOpenAIKey && (
          <div className="input-warning">
            <span>⚠️ OpenAI API 키를 먼저 설정해주세요.</span>
          </div>
        )}
      </div>
    </div>
  );
}

export default ChatComponent;
