// App.js
// 전체 앱 엔트리포인트
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import ChatComponent from './components/ChatComponent';
import DatabaseManager from './components/DatabaseManager';
import MonitoringComponent from './components/MonitoringComponent';
import SlowQueryComponent from './components/SlowQueryComponent';
import AwsIntegrationComponent from './components/AwsIntegrationComponent';
import OpenAIManager from './components/OpenAIManager';
import PlaybookComponent from './components/PlaybookComponent';
import AwsRdsResource from './components/AwsRdsResource';
import AwsCloudwatchResource from './components/AwsCloudwatchResource';

function App() {
  const [databases, setDatabases] = useState([]);
  const [selectedDb, setSelectedDb] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAwsSubMenu, setShowAwsSubMenu] = useState(false);

  // 네비게이션 메뉴
  const menuItems = [
    { id: 'chat', label: '💬 AI 채팅', icon: '💬', path: '/chat' },
    { id: 'databases', label: '🗄️ DB 관리', icon: '🗄️', path: '/databases' },
    { id: 'openai', label: '🔑 OpenAI 설정', icon: '🔑', path: '/openai' },
    { id: 'playbooks', label: '📖 플레이북', icon: '📖', path: '/playbooks' },
    { id: 'monitoring', label: '📊 모니터링', icon: '📊', path: '/monitoring' },
    { id: 'slowquery', label: '🐌 슬로우 쿼리', icon: '🐌', path: '/slowquery' },
    { id: 'aws', label: '☁️ AWS 통합', icon: '☁️', path: '/aws' },
    { id: 'aws-resources', label: '☁️ AWS 리소스 조회', icon: '☁️',
      children: [
        { id: 'aws-rds', label: 'RDS 조회', icon: '🗄️', path: '/aws-rds' },
        { id: 'aws-cloudwatch', label: 'CloudWatch 조회', icon: '📊', path: '/aws-cloudwatch' }
      ]
    }
  ];

  // DB 목록 가져오기
  const fetchDatabases = async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/databases');
      if (response.ok) {
        const data = await response.json();
        setDatabases(data.databases || []);
        if (data.databases && data.databases.length > 0 && !selectedDb) {
          setSelectedDb(data.databases[0].name);
        }
      } else {
        console.error('DB 목록 가져오기 실패');
      }
    } catch (error) {
      console.error('DB 목록 요청 오류:', error);
      setError('데이터베이스 목록을 가져오는 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  // DB 변경 처리
  const handleDatabaseChange = () => {
    fetchDatabases();
  };

  // DB 삭제 처리
  const handleDatabaseDelete = (dbName) => {
    setDatabases(prev => prev.filter(db => db.name !== dbName));
    if (selectedDb === dbName) {
      setSelectedDb('');
    }
  };

  // DB 추가 처리
  const handleDatabaseAdd = () => {
    fetchDatabases();
  };

  useEffect(() => {
    fetchDatabases();
  }, []);

  // 라우터 네비게이션 헬퍼
  function SidebarNav() {
    const navigate = useNavigate();
    const location = useLocation();
    return (
      <nav className="sidebar-nav">
        {menuItems.map(item => (
          item.children ? (
            <div key={item.id}>
              <button
                onClick={() => setShowAwsSubMenu(!showAwsSubMenu)}
                className={`nav-item ${location.pathname.startsWith('/aws-') ? 'active' : ''}`}
              >
                <span className="nav-icon">{item.icon}</span>
                <span className="nav-label">{item.label}</span>
              </button>
              {showAwsSubMenu && (
                <div className="submenu">
                  {item.children.map(sub => (
                    <button
                      key={sub.id}
                      onClick={() => navigate(sub.path)}
                      className={`nav-item sub ${location.pathname === sub.path ? 'active' : ''}`}
                    >
                      <span className="nav-icon">{sub.icon}</span>
                      <span className="nav-label">{sub.label}</span>
                    </button>
                  ))}
                </div>
              )}
            </div>
          ) : (
            <button
              key={item.id}
              onClick={() => navigate(item.path)}
              className={`nav-item ${location.pathname === item.path ? 'active' : ''}`}
            >
              <span className="nav-icon">{item.icon}</span>
              <span className="nav-label">{item.label}</span>
            </button>
          )
        ))}
      </nav>
    );
  }

  if (loading && databases.length === 0) {
    return (
      <div className="app-loading">
        <div className="loading-spinner"></div>
        <p>데이터베이스 정보를 불러오는 중...</p>
      </div>
    );
  }

  return (
    <Router>
      <div className="app">
        {/* 사이드바 네비게이션 */}
        <div className="sidebar">
          <div className="sidebar-header">
            <h1>🚀 DB Agent</h1>
            <p>SRE/DBA용 AI 도구</p>
          </div>
          <SidebarNav />
          {/* DB 선택 (채팅, 모니터링, 슬로우 쿼리, 플레이북에서만 표시) */}
          {["/chat", "/monitoring", "/slowquery", "/playbooks"].includes(window.location.pathname) && (
            <div className="db-selector">
              <label htmlFor="global-db-select">현재 DB:</label>
              <select
                id="global-db-select"
                value={selectedDb}
                onChange={(e) => setSelectedDb(e.target.value)}
                className="db-select"
              >
                <option value="">DB를 선택하세요</option>
                {databases.map(db => (
                  <option key={db.name} value={db.name}>
                    {db.name}
                  </option>
                ))}
              </select>
            </div>
          )}
        </div>
        <div className="main-content">
          <Routes>
            <Route path="/chat" element={<ChatComponent selectedDb={selectedDb} databases={databases} onDbChange={setSelectedDb} onDatabaseChange={handleDatabaseChange} />} />
            <Route path="/databases" element={<DatabaseManager selectedDb={selectedDb} databases={databases} onDatabaseDelete={handleDatabaseDelete} onDatabaseAdd={handleDatabaseAdd} onDatabaseChange={handleDatabaseChange} />} />
            <Route path="/openai" element={<OpenAIManager />} />
            <Route path="/playbooks" element={<PlaybookComponent selectedDb={selectedDb} databases={databases} onDatabaseChange={handleDatabaseChange} />} />
            <Route path="/monitoring" element={<MonitoringComponent selectedDb={selectedDb} databases={databases} onDatabaseChange={handleDatabaseChange} />} />
            <Route path="/slowquery" element={<SlowQueryComponent selectedDb={selectedDb} databases={databases} onDatabaseChange={handleDatabaseChange} />} />
            <Route path="/aws" element={<AwsIntegrationComponent selectedDb={selectedDb} databases={databases} />} />
            <Route path="/aws-rds" element={<AwsRdsResource onDatabaseAdd={handleDatabaseAdd} />} />
            <Route path="/aws-cloudwatch" element={<AwsCloudwatchResource />} />
            <Route path="*" element={<ChatComponent selectedDb={selectedDb} databases={databases} onDbChange={setSelectedDb} onDatabaseChange={handleDatabaseChange} />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
