import React, { useState, useEffect } from 'react';

const PlaybookComponent = ({ selectedDb, databases }) => {
  const [playbooks, setPlaybooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // ÌîåÎ†àÏù¥Î∂Å Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchPlaybooks = async () => {
    setLoading(true);
    try {
      const response = await fetch('/playbooks');
      if (response.ok) {
        const html = await response.text();
        // HTMLÏóêÏÑú ÌîåÎ†àÏù¥Î∂Å Ï†ïÎ≥¥Î•º ÌååÏã± (Ïã§Ï†úÎ°úÎäî API ÏóîÎìúÌè¨Ïù∏Ìä∏Í∞Ä ÌïÑÏöî)
        // ÏûÑÏãúÎ°ú playbooks.json ÌååÏùºÏùÑ ÏßÅÏ†ë Í∞ÄÏ†∏Ïò§Í∏∞
        const playbooksResponse = await fetch('/playbooks.json');
        if (playbooksResponse.ok) {
          const playbooksData = await playbooksResponse.json();
          setPlaybooks(playbooksData);
        }
      }
    } catch (error) {
      console.error('ÌîåÎ†àÏù¥Î∂Å Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®:', error);
      setError('ÌîåÎ†àÏù¥Î∂Å Î™©Î°ùÏùÑ Í∞ÄÏ†∏Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    } finally {
      setLoading(false);
    }
  };

  // ÌîåÎ†àÏù¥Î∂Å Ïã§Ìñâ
  const handleRunPlaybook = async (playbookName) => {
    if (!selectedDb) {
      setError('Î®ºÏ†Ä Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§Î•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('playbook_name', playbookName);
      formData.append('db_name', selectedDb);

      const response = await fetch('/playbooks/run', {
        method: 'POST',
        body: formData
      });

      if (response.ok) {
        setSuccess(`ÌîåÎ†àÏù¥Î∂Å "${playbookName}"Ïù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ïã§ÌñâÎêòÏóàÏäµÎãàÎã§. AI Ï±ÑÌåÖÏóêÏÑú Í≤∞Í≥ºÎ•º ÌôïÏù∏ÌïòÏÑ∏Ïöî.`);
        setError('');

        // AI Ï±ÑÌåÖ ÌéòÏù¥ÏßÄÎ°ú Î¶¨Îã§Ïù¥Î†âÌä∏
        setTimeout(() => {
          window.location.href = '/nl2sql';
        }, 2000);
      } else {
        setError('ÌîåÎ†àÏù¥Î∂Å Ïã§ÌñâÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    } catch (error) {
      setError('ÌîåÎ†àÏù¥Î∂Å Ïã§Ìñâ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPlaybooks();
  }, []);

  return (
    <div className="playbook-component">
      <div className="section-header">
        <h2>üìñ ÌîåÎ†àÏù¥Î∂Å</h2>
        <p>ÏûêÏ£º ÏÇ¨Ïö©ÌïòÎäî Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ ÏûëÏóÖÏùÑ ÏûêÎèôÌôîÎêú ÌîåÎ†àÏù¥Î∂ÅÏúºÎ°ú Ïã§ÌñâÌïòÏÑ∏Ïöî.</p>
      </div>

      {/* ÏÉÅÌÉú Î©îÏãúÏßÄ */}
      {error && (
        <div className="alert alert-error">
          <span>‚ö†Ô∏è {error}</span>
          <button onClick={() => setError('')} className="close-btn">‚úï</button>
        </div>
      )}

      {success && (
        <div className="alert alert-success">
          <span>‚úÖ {success}</span>
          <button onClick={() => setSuccess('')} className="close-btn">‚úï</button>
        </div>
      )}

      {/* DB ÏÑ†ÌÉù ÏïàÎÇ¥ */}
      {!selectedDb && (
        <div className="alert alert-warning">
          <span>‚ö†Ô∏è ÌîåÎ†àÏù¥Î∂ÅÏùÑ Ïã§ÌñâÌïòÎ†§Î©¥ Î®ºÏ†Ä Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§Î•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.</span>
        </div>
      )}

      {/* ÌîåÎ†àÏù¥Î∂Å Î™©Î°ù */}
      <div className="playbook-grid">
        {loading ? (
          <div className="loading-state">
            <div className="loading-spinner"></div>
            <p>ÌîåÎ†àÏù¥Î∂ÅÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë...</p>
          </div>
        ) : playbooks.length === 0 ? (
          <div className="empty-state">
            <p>ÏÇ¨Ïö© Í∞ÄÎä•Ìïú ÌîåÎ†àÏù¥Î∂ÅÏù¥ ÏóÜÏäµÎãàÎã§.</p>
            <p>ÌîåÎ†àÏù¥Î∂ÅÏùÑ Ï∂îÍ∞ÄÌïòÎ†§Î©¥ playbooks.json ÌååÏùºÏùÑ ÌôïÏù∏ÌïòÏÑ∏Ïöî.</p>
          </div>
        ) : (
          playbooks.map((playbook, index) => (
            <div key={index} className="playbook-card">
              <div className="playbook-header">
                <h3>{playbook.name}</h3>
                <span className="playbook-category">{playbook.category}</span>
              </div>

              <div className="playbook-description">
                <p>{playbook.description}</p>
              </div>

              <div className="playbook-steps">
                <h4>Ïã§Ìñâ Îã®Í≥Ñ:</h4>
                <ol>
                  {playbook.steps.map((step, stepIndex) => (
                    <li key={stepIndex}>
                      <strong>{step.title}</strong>
                      <p>{step.prompt}</p>
                    </li>
                  ))}
                </ol>
              </div>

              <div className="playbook-actions">
                <button
                  onClick={() => handleRunPlaybook(playbook.name)}
                  className="btn btn-primary"
                  disabled={loading || !selectedDb}
                >
                  {loading ? 'Ïã§Ìñâ Ï§ë...' : 'ÌîåÎ†àÏù¥Î∂Å Ïã§Ìñâ'}
                </button>

                <div className="playbook-info">
                  <span>ÏòàÏÉÅ ÏãúÍ∞Ñ: {playbook.estimatedTime || '1-2Î∂Ñ'}</span>
                  <span>ÎÇúÏù¥ÎèÑ: {playbook.difficulty || 'Î≥¥ÌÜµ'}</span>
                </div>
              </div>
            </div>
          ))
        )}
      </div>

      {/* ÏÇ¨Ïö©Î≤ï ÏïàÎÇ¥ */}
      <div className="card">
        <h3>üìñ ÌîåÎ†àÏù¥Î∂Å ÏÇ¨Ïö©Î≤ï</h3>
        <div className="usage-guide">
          <ol>
