// App.js
// Ï†ÑÏ≤¥ Ïï± ÏóîÌä∏Î¶¨Ìè¨Ïù∏Ìä∏
import React, { useState, useEffect } from 'react';
import ChatComponent from './components/ChatComponent';
import DatabaseManager from './components/DatabaseManager';
import MonitoringComponent from './components/MonitoringComponent';
import SlowQueryComponent from './components/SlowQueryComponent';
import AwsIntegrationComponent from './components/AwsIntegrationComponent';
import OpenAIManager from './components/OpenAIManager';
import PlaybookComponent from './components/PlaybookComponent';

function App() {
  const [currentView, setCurrentView] = useState('chat');
  const [databases, setDatabases] = useState([]);
  const [selectedDb, setSelectedDb] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò Î©îÎâ¥
  const menuItems = [
    { id: 'chat', label: 'üí¨ AI Ï±ÑÌåÖ', icon: 'üí¨' },
    { id: 'databases', label: 'üóÑÔ∏è DB Í¥ÄÎ¶¨', icon: 'üóÑÔ∏è' },
    { id: 'openai', label: 'üîë OpenAI ÏÑ§Ï†ï', icon: 'üîë' },
    { id: 'playbooks', label: 'üìñ ÌîåÎ†àÏù¥Î∂Å', icon: 'üìñ' },
    { id: 'monitoring', label: 'üìä Î™®ÎãàÌÑ∞ÎßÅ', icon: 'üìä' },
    { id: 'slowquery', label: 'üêå Ïä¨Î°úÏö∞ ÏøºÎ¶¨', icon: 'üêå' },
    { id: 'aws', label: '‚òÅÔ∏è AWS ÌÜµÌï©', icon: '‚òÅÔ∏è' }
  ];

  // DB Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchDatabases = async () => {
    setLoading(true);
    try {
      const response = await fetch('/databases');
      if (response.ok) {
        const html = await response.text();
        // HTMLÏóêÏÑú DB Ï†ïÎ≥¥Î•º ÌååÏã± (Ïã§Ï†úÎ°úÎäî API ÏóîÎìúÌè¨Ïù∏Ìä∏Í∞Ä ÌïÑÏöî)
        // ÏûÑÏãúÎ°ú ÏÑ∏ÏÖòÏä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú Í∞ÄÏ†∏Ïò§Í∏∞
        const dbData = JSON.parse(localStorage.getItem('databases') || '[]');
        setDatabases(dbData);

        // Ï≤´ Î≤àÏß∏ DBÎ•º ÏûêÎèô ÏÑ†ÌÉù (ÏûàÎäî Í≤ΩÏö∞)
        if (dbData.length > 0 && !selectedDb) {
          setSelectedDb(dbData[0].name);
        }
      } else {
        console.error('DB Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®');
      }
    } catch (error) {
      console.error('DB Î™©Î°ù ÏöîÏ≤≠ Ïò§Î•ò:', error);
      setError('Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Î™©Î°ùÏùÑ Í∞ÄÏ†∏Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    } finally {
      setLoading(false);
    }
  };

  // DB Î≥ÄÍ≤Ω Ï≤òÎ¶¨
  const handleDatabaseChange = () => {
    fetchDatabases();
  };

  // DB ÏÇ≠Ï†ú Ï≤òÎ¶¨
  const handleDatabaseDelete = (dbName) => {
    setDatabases(prev => prev.filter(db => db.name !== dbName));
    if (selectedDb === dbName) {
      setSelectedDb('');
    }
  };

  // DB Ï∂îÍ∞Ä Ï≤òÎ¶¨
  const handleDatabaseAdd = () => {
    fetchDatabases();
  };

  // Ï¥àÍ∏∞ Î°úÎìú
  useEffect(() => {
    fetchDatabases();
  }, []);

  // ÌòÑÏû¨ Î∑∞Ïóê Îî∞Î•∏ Ïª¥Ìè¨ÎÑåÌä∏ Î†åÎçîÎßÅ
  const renderCurrentView = () => {
    const commonProps = {
      selectedDb,
      databases,
      onDatabaseChange: handleDatabaseChange
    };

    switch (currentView) {
      case 'chat':
        return (
          <ChatComponent
            {...commonProps}
            onDbChange={setSelectedDb}
          />
        );
      case 'databases':
        return (
          <DatabaseManager
            {...commonProps}
            onDatabaseDelete={handleDatabaseDelete}
            onDatabaseAdd={handleDatabaseAdd}
          />
        );
      case 'openai':
        return <OpenAIManager />;
      case 'playbooks':
        return <PlaybookComponent {...commonProps} />;
      case 'monitoring':
        return <MonitoringComponent {...commonProps} />;
      case 'slowquery':
        return <SlowQueryComponent {...commonProps} />;
      case 'aws':
        return <AwsIntegrationComponent {...commonProps} />;
      default:
        return <ChatComponent {...commonProps} />;
    }
  };

  // Î°úÎî© ÏÉÅÌÉú
  if (loading && databases.length === 0) {
    return (
      <div className="app-loading">
        <div className="loading-spinner"></div>
        <p>Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p>
      </div>
    );
  }

  return (
    <div className="app">
      {/* ÏÇ¨Ïù¥ÎìúÎ∞î ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò */}
      <div className="sidebar">
        <div className="sidebar-header">
          <h1>üöÄ DB Agent</h1>
          <p>SRE/DBAÏö© AI ÎèÑÍµ¨</p>
        </div>

        <nav className="sidebar-nav">
          {menuItems.map(item => (
            <button
              key={item.id}
              onClick={() => setCurrentView(item.id)}
              className={`nav-item ${currentView === item.id ? 'active' : ''}`}
            >
              <span className="nav-icon">{item.icon}</span>
              <span className="nav-label">{item.label}</span>
            </button>
          ))}
        </nav>

        {/* DB ÏÑ†ÌÉù (Ï±ÑÌåÖ, Î™®ÎãàÌÑ∞ÎßÅ, Ïä¨Î°úÏö∞ ÏøºÎ¶¨, ÌîåÎ†àÏù¥Î∂ÅÏóêÏÑúÎßå ÌëúÏãú) */}
        {['chat', 'monitoring', 'slowquery', 'playbooks'].includes(currentView) && (
          <div className="db-selector">
            <label htmlFor="global-db-select">ÌòÑÏû¨ DB:</label>
            <select
              id="global-db-select"
              value={selectedDb}
              onChange={(e) => setSelectedDb(e.target.value)}
