// MonitoringComponent.js
// DBÎ≥Ñ Ïã§ÏãúÍ∞Ñ Î™®ÎãàÌÑ∞ÎßÅ, Ï∞®Ìä∏, ÏÉÅÏÑ∏ ÌéòÏù¥ÏßÄ
import React, { useState, useEffect, useRef, useCallback } from 'react';

const CLOUDWATCH_METRICS = [
  { key: 'connections', name: 'DatabaseConnections', label: 'Ïó∞Í≤∞ Ïàò', unit: 'Í∞ú', color: '#007bff' },
  { key: 'cpu', name: 'CPUUtilization', label: 'CPU ÏÇ¨Ïö©Î•†', unit: '%', color: '#28a745' },
  { key: 'memory', name: 'FreeableMemory', label: 'ÎÇ®ÏùÄ Î©îÎ™®Î¶¨', unit: 'GB', color: '#ffc107' }, // ÏàòÏ†ï: Ïπ¥Îìú Ï†úÎ™©/Îã®ÏúÑ
  { key: 'storage', name: 'FreeStorageSpace', label: 'ÏÇ¨Ïö© Í∞ÄÎä• Ïä§ÌÜ†Î¶¨ÏßÄ', unit: 'GB', color: '#6f42c1' },
  { key: 'read_iops', name: 'ReadIOPS', label: 'ÏùΩÍ∏∞ IOPS', unit: '', color: '#17a2b8' },
  { key: 'write_iops', name: 'WriteIOPS', label: 'Ïì∞Í∏∞ IOPS', unit: '', color: '#fd7e14' }
];

function MonitoringComponent({ selectedDb, databases, onDbChange }) {
  const [metrics, setMetrics] = useState({});
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [timeRange, setTimeRange] = useState('1h');
  const [refreshInterval, setRefreshInterval] = useState(30);
  const [isAutoRefresh, setIsAutoRefresh] = useState(true);
  const intervalRef = useRef(null);

  const timeRanges = [
    { value: '1h', label: '1ÏãúÍ∞Ñ' },
    { value: '6h', label: '6ÏãúÍ∞Ñ' },
    { value: '24h', label: '24ÏãúÍ∞Ñ' },
    { value: '7d', label: '7Ïùº' }
  ];

  const refreshIntervals = [
    { value: 10, label: '10Ï¥à' },
    { value: 30, label: '30Ï¥à' },
    { value: 60, label: '1Î∂Ñ' },
    { value: 300, label: '5Î∂Ñ' }
  ];

  // ÏãúÍ∞Ñ Î≤îÏúÑ ‚Üí hours Î≥ÄÌôò
  const getHours = (range) => {
    switch (range) {
      case '1h': return 1;
      case '6h': return 6;
      case '24h': return 24;
      case '7d': return 24 * 7;
      default: return 1;
    }
  };

  // CloudWatch Î©îÌä∏Î¶≠ Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchCloudwatchMetrics = useCallback(async () => {
    if (!selectedDb) return;
    setLoading(true);
    try {
      const dbIdentifier = selectedDb;
      const hours = getHours(timeRange);
      const results = await Promise.all(
        CLOUDWATCH_METRICS.map(async (metric) => {
          const url = `/api/monitoring/cloudwatch/metrics/${dbIdentifier}?metric_name=${metric.name}&hours=${hours}`;
          let errorMsg = null;
          let json = { data: [] };
          let status = 200;
          try {
            const res = await fetch(url);
            status = res.status;
            try {
              json = await res.json();
            } catch (e) {}
            if (!res.ok) {
              errorMsg = (json && json.detail) ? json.detail : `API Ïò§Î•ò (status ${status})`;
            }
          } catch (e) {
            errorMsg = e.message || 'API ÏöîÏ≤≠ Ïã§Ìå®';
          }
          // ÏΩòÏÜîÏóê ÏùëÎãµ(raw) Ï∂úÎ†•
          console.log(`[CloudWatch][${metric.name}]`, json, errorMsg);
          return { key: metric.key, data: json.data || [], error: errorMsg };
        })
      );
      const newMetrics = {};
      const newErrors = {};
      results.forEach(({ key, data, error }) => {
        newMetrics[key] = data;
        if (error) newErrors[key] = error;
      });
      setMetrics(newMetrics);
      setErrors(newErrors);
    } catch (error) {
      console.error('CloudWatch Î©îÌä∏Î¶≠ ÏöîÏ≤≠ Ïò§Î•ò:', error);
    } finally {
      setLoading(false);
    }
  }, [selectedDb, timeRange]);

  // ÏûêÎèô ÏÉàÎ°úÍ≥†Ïπ®
  useEffect(() => {
    if (isAutoRefresh && selectedDb) {
      fetchCloudwatchMetrics();
      intervalRef.current = setInterval(() => {
        fetchCloudwatchMetrics();
      }, refreshInterval * 1000);
      return () => {
        if (intervalRef.current) clearInterval(intervalRef.current);
      };
    } else {
      if (intervalRef.current) clearInterval(intervalRef.current);
    }
  }, [selectedDb, timeRange, refreshInterval, isAutoRefresh, fetchCloudwatchMetrics]);

  // ÏàòÎèô ÏÉàÎ°úÍ≥†Ïπ®
  const handleManualRefresh = () => {
    fetchCloudwatchMetrics();
  };

  // Ï∞®Ìä∏ Îç∞Ïù¥ÌÑ∞ Ìè¨Îß∑ÌåÖ
  const formatChartData = (data, metricKey) => {
    if (!data || !Array.isArray(data)) return [];
    // Î©îÎ™®Î¶¨/Ïä§ÌÜ†Î¶¨ÏßÄÎäî Îã®ÏúÑ Î≥ÄÌôò
    if (metricKey === 'memory') {
      return data.map(point => ({ x: new Date(point.timestamp), y: Math.round(point.value / 1024 / 1024) })); // MB
    }
    if (metricKey === 'storage') {
      return data.map(point => ({ x: new Date(point.timestamp), y: Math.round(point.value / 1024 / 1024 / 1024) })); // GB
    }
    return data.map(point => ({ x: new Date(point.timestamp), y: point.value }));
  };

  // Ïπ¥Îìú/Ï∞®Ìä∏ UI Í∞úÏÑ† (ÏóêÎü¨ Î©îÏãúÏßÄ ÌëúÏãú)
  const renderMetricCard = (title, value, unit = '', color = '#007bff', error = null) => (
    <div className="metric-card improved">
      <div className="metric-header">
        <h4 style={{ color }}>{title}</h4>
      </div>
      <div className="metric-value">
        {value === null || value === undefined || isNaN(value) ? (
          <span className="no-data">Îç∞Ïù¥ÌÑ∞ ÏóÜÏùå</span>
        ) : (
          <span className="value" style={{ color }}>{value}</span>
        )}
        {unit && <span className="unit">{unit}</span>}
      </div>
      {error && <div className="metric-error">{error}</div>}
    </div>
  );

  const renderChart = (title, data, color = '#007bff', unit = '', error = null) => (
    <div className="chart-card improved">
      <h4 style={{ color }}>{title}</h4>
      <div className="chart-container">
        {error ? (
          <div className="no-data metric-error">{error}</div>
        ) : (!data || data.length === 0) ? (
          <div className="no-data">Îç∞Ïù¥ÌÑ∞ ÏóÜÏùå</div>
        ) : (
          <svg width="100%" height="200" viewBox="0 0 400 200">
            {data.length > 1 && (
              <polyline
                fill="none"
                stroke={color}
                strokeWidth="2"
                points={data.map((point, i) =>
                  `${(i / (data.length - 1)) * 400},${200 - (point.y / Math.max(...data.map(p => p.y) || [1])) * 180}`
                ).join(' ')}
              />
            )}
            {data.map((point, i) => (
              <circle
                key={i}
                cx={(i / (data.length - 1)) * 400}
                cy={200 - (point.y / Math.max(...data.map(p => p.y) || [1])) * 180}
                r="3"
                fill={color}
              />
            ))}
          </svg>
        )}
        {unit && <div className="chart-unit">({unit})</div>}
      </div>
    </div>
  );

  // cloudwatch_id ÌëúÏãúÏö©
  const selectedDbObj = databases?.find(db => db.name === selectedDb);
  const cloudwatchId = selectedDbObj?.cloudwatch_id;

  return (
    <div className="monitoring-component improved">
      {/* Ìó§Îçî */}
      <div className="monitoring-header">
        <div className="header-left">
          <h2>Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Î™®ÎãàÌÑ∞ÎßÅ (CloudWatch)</h2>
          {selectedDb && (
            <span className="selected-db">
              ÏÑ†ÌÉùÎêú DB: {selectedDb}
              {cloudwatchId && (
                <span style={{ fontSize: '13px', color: '#888', marginLeft: 12 }}>
                  (CloudWatch ID: <b>{cloudwatchId}</b>)
                </span>
              )}
            </span>
          )}
        </div>
        <div className="header-controls">
          {/* DB ÏÑ†ÌÉù */}
          <select 
            value={selectedDb || ''} 
            onChange={(e) => onDbChange(e.target.value)}
            className="db-select"
          >
            <option value="">DB ÏÑ†ÌÉù</option>
            {databases?.map(db => (
              <option key={db.name} value={db.name}>
                {db.name}
              </option>
            ))}
          </select>
          {/* ÏãúÍ∞Ñ Î≤îÏúÑ */}
          <select 
            value={timeRange} 
            onChange={(e) => setTimeRange(e.target.value)}
            className="time-range-select"
          >
            {timeRanges.map(range => (
              <option key={range.value} value={range.value}>
                {range.label}
              </option>
            ))}
          </select>
          {/* ÏÉàÎ°úÍ≥†Ïπ® Í∞ÑÍ≤© */}
          <select 
            value={refreshInterval} 
            onChange={(e) => setRefreshInterval(Number(e.target.value))}
            className="refresh-interval-select"
          >
            {refreshIntervals.map(interval => (
              <option key={interval.value} value={interval.value}>
                {interval.label}
              </option>
            ))}
          </select>
          {/* ÏûêÎèô ÏÉàÎ°úÍ≥†Ïπ® ÌÜ†Í∏Ä */}
          <label className="auto-refresh-toggle">
            <input
              type="checkbox"
              checked={isAutoRefresh}
              onChange={(e) => setIsAutoRefresh(e.target.checked)}
            />
            ÏûêÎèô ÏÉàÎ°úÍ≥†Ïπ®
          </label>
          {/* ÏàòÎèô ÏÉàÎ°úÍ≥†Ïπ® */}
          <button 
            onClick={handleManualRefresh}
            disabled={loading || !selectedDb}
            className="btn btn-outline"
          >
            {loading ? 'ÏÉàÎ°úÍ≥†Ïπ® Ï§ë...' : 'üîÑ ÏÉàÎ°úÍ≥†Ïπ®'}
          </button>
        </div>
      </div>
      {!selectedDb ? (
        <div className="no-db-selected">
          <p>Î™®ÎãàÌÑ∞ÎßÅÌï† Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§Î•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.</p>
        </div>
      ) : (
        <div className="monitoring-content">
          {/* 4Îã® Ïπ¥Îìú+Í∑∏ÎûòÌîÑ ÏùºÏ≤¥Ìòï Í∑∏Î¶¨Îìú */}
          <div className="metrics-grid improved">
            {CLOUDWATCH_METRICS.map(metric => {
              const data = metrics[metric.key] || [];
              const latest = data.length > 0 ? data[data.length - 1].value : null;
              let displayValue = latest;
              // Î©îÎ™®Î¶¨(FreeableMemory)Îäî GB Îã®ÏúÑÎ°ú Î≥ÄÌôò, ÏÜåÏàòÏ†ê 1ÏûêÎ¶¨
              if (metric.key === 'memory' && latest !== null) displayValue = (latest / 1024 / 1024 / 1024).toFixed(1); // GB
              if (metric.key === 'storage' && latest !== null) displayValue = Math.round(latest); // GB
              const chartData = metric.key === 'memory'
                ? (data || []).map(point => ({ x: new Date(point.timestamp), y: +(point.value / 1024 / 1024 / 1024).toFixed(1) })) // GB
                : formatChartData(data, metric.key);
              return (
                <div key={metric.key} style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
                  {renderMetricCard(metric.label, displayValue, metric.unit, metric.color, errors[metric.key])}
                  {renderChart('', chartData, metric.color, metric.unit, errors[metric.key])}
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
}

export default MonitoringComponent;
