// SlowQueryComponent.js
// CloudWatch/RDS Ïä¨Î°úÏö∞ ÏøºÎ¶¨ Î∂ÑÏÑù, ÏãúÍ∞ÅÌôî
import React, { useState, useEffect } from 'react';

function SlowQueryComponent({ selectedDb, databases, onDbChange }) {
  const [slowQueries, setSlowQueries] = useState([]);
  const [loading, setLoading] = useState(false);
  const [filters, setFilters] = useState({
    timeRange: '24h',
    minDuration: 1, // Ï¥à Îã®ÏúÑ
    limit: 50,
    sortBy: 'duration', // duration, count, avg_duration
    sortOrder: 'desc'
  });
  const [selectedQuery, setSelectedQuery] = useState(null);
  const [queryDetails, setQueryDetails] = useState(null);
  const [showDetails, setShowDetails] = useState(false);

  const timeRanges = [
    { value: '1h', label: '1ÏãúÍ∞Ñ' },
    { value: '6h', label: '6ÏãúÍ∞Ñ' },
    { value: '24h', label: '24ÏãúÍ∞Ñ' },
    { value: '7d', label: '7Ïùº' },
    { value: '30d', label: '30Ïùº' }
  ];

  const sortOptions = [
    { value: 'duration', label: 'Ïã§Ìñâ ÏãúÍ∞Ñ' },
    { value: 'count', label: 'Ïã§Ìñâ ÌöüÏàò' },
    { value: 'avg_duration', label: 'ÌèâÍ∑† Ïã§Ìñâ ÏãúÍ∞Ñ' },
    { value: 'rows_examined', label: 'Í≤ÄÏÇ¨Îêú Ìñâ Ïàò' },
    { value: 'rows_sent', label: 'Î∞òÌôòÎêú Ìñâ Ïàò' }
  ];

  // Ïä¨Î°úÏö∞ ÏøºÎ¶¨ Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchSlowQueries = async () => {
    if (!selectedDb) return;

    setLoading(true);
    try {
      const params = new URLSearchParams({
        db_name: selectedDb,
        ...filters
      });

      const response = await fetch(`/slowquery/list?${params}`);
      if (response.ok) {
        const data = await response.json();
        setSlowQueries(data.queries || []);
      } else {
        console.error('Ïä¨Î°úÏö∞ ÏøºÎ¶¨ Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®');
      }
    } catch (error) {
      console.error('Ïä¨Î°úÏö∞ ÏøºÎ¶¨ ÏöîÏ≤≠ Ïò§Î•ò:', error);
    } finally {
      setLoading(false);
    }
  };

  // ÏøºÎ¶¨ ÏÉÅÏÑ∏ Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchQueryDetails = async (queryId) => {
    try {
      const response = await fetch(`/slowquery/details?db_name=${selectedDb}&query_id=${queryId}`);
      if (response.ok) {
        const data = await response.json();
        setQueryDetails(data);
        setShowDetails(true);
      }
    } catch (error) {
      console.error('ÏøºÎ¶¨ ÏÉÅÏÑ∏ Ï†ïÎ≥¥ ÏöîÏ≤≠ Ïò§Î•ò:', error);
    }
  };

  // ÌïÑÌÑ∞ Î≥ÄÍ≤Ω Ï≤òÎ¶¨
  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value
    }));
  };

  // ÏøºÎ¶¨ ÏÑ†ÌÉù Ï≤òÎ¶¨
  const handleQuerySelect = (query) => {
    setSelectedQuery(query);
    fetchQueryDetails(query.id);
  };

  // ÏøºÎ¶¨ Ïã§Ìñâ Í≥ÑÌöç Í∞ÄÏ†∏Ïò§Í∏∞
  const getExecutionPlan = async (sql) => {
    try {
      const response = await fetch('/slowquery/explain', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          db_name: selectedDb,
          sql: sql
        })
      });

      if (response.ok) {
        const data = await response.json();
        return data.plan;
      }
    } catch (error) {
      console.error('Ïã§Ìñâ Í≥ÑÌöç ÏöîÏ≤≠ Ïò§Î•ò:', error);
    }
    return null;
  };

  // SQL Ìè¨Îß∑ÌåÖ
  const formatSQL = (sql) => {
    if (!sql) return '';

    // Í∏∞Î≥∏Ï†ÅÏù∏ SQL Ìè¨Îß∑ÌåÖ
    return sql
      .replace(/\b(SELECT|FROM|WHERE|AND|OR|ORDER BY|GROUP BY|HAVING|LIMIT|JOIN|LEFT|RIGHT|INNER|OUTER)\b/gi, '\n$1')
      .replace(/\b(INSERT|UPDATE|DELETE|CREATE|ALTER|DROP|INDEX|TABLE|DATABASE)\b/gi, '\n$1')
      .trim();
  };

  // ÏãúÍ∞Ñ Ìè¨Îß∑ÌåÖ
  const formatDuration = (seconds) => {
    if (seconds < 1) {
      return `${(seconds * 1000).toFixed(2)}ms`;
    } else if (seconds < 60) {
      return `${seconds.toFixed(2)}s`;
    } else {
      const minutes = Math.floor(seconds / 60);
      const remainingSeconds = seconds % 60;
      return `${minutes}m ${remainingSeconds.toFixed(2)}s`;
    }
  };

  // Îç∞Ïù¥ÌÑ∞ Ìè¨Îß∑ÌåÖ
  const formatNumber = (num) => {
    if (num >= 1000000) {
      return `${(num / 1000000).toFixed(1)}M`;
    } else if (num >= 1000) {
      return `${(num / 1000).toFixed(1)}K`;
    }
    return num.toLocaleString();
  };

  // ÌïÑÌÑ∞ Ï†ÅÏö© Ïãú Îç∞Ïù¥ÌÑ∞ Îã§Ïãú Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    fetchSlowQueries();
  }, [selectedDb, filters]);

  // ÏøºÎ¶¨ Î∂ÑÏÑù Î∞è ÏµúÏ†ÅÌôî Ï†úÏïà
  const analyzeQuery = (query) => {
    const suggestions = [];

    // Í∏∞Î≥∏Ï†ÅÏù∏ Î∂ÑÏÑù
    if (query.rows_examined > query.rows_sent * 10) {
      suggestions.push('Ïù∏Îç±Ïä§ Ï∂îÍ∞ÄÎ•º Í≥†Î†§Ìï¥Î≥¥ÏÑ∏Ïöî. Í≤ÄÏÇ¨Îêú Ìñâ ÏàòÍ∞Ä Î∞òÌôòÎêú Ìñâ ÏàòÎ≥¥Îã§ Ìõ®Ïî¨ ÎßéÏäµÎãàÎã§.');
    }

    if (query.sql?.toLowerCase().includes('select *')) {
      suggestions.push('SELECT * ÎåÄÏã† ÌïÑÏöîÌïú Ïª¨ÎüºÎßå ÏÑ†ÌÉùÌïòÎäî Í≤ÉÏùÑ Í≥†Î†§Ìï¥Î≥¥ÏÑ∏Ïöî.');
    }

    if (query.sql?.toLowerCase().includes('like %')) {
      suggestions.push('LIKE Ìå®ÌÑ¥Ïù¥ %Î°ú ÏãúÏûëÌïòÎ©¥ Ïù∏Îç±Ïä§Î•º ÏÇ¨Ïö©Ìï† Ïàò ÏóÜÏäµÎãàÎã§. Îã§Î•∏ Í≤ÄÏÉâ Î∞©Î≤ïÏùÑ Í≥†Î†§Ìï¥Î≥¥ÏÑ∏Ïöî.');
    }

    if (query.count > 100) {
      suggestions.push('Ïù¥ ÏøºÎ¶¨Í∞Ä Îß§Ïö∞ ÏûêÏ£º Ïã§ÌñâÎêòÍ≥† ÏûàÏäµÎãàÎã§. Ï∫êÏã±Ïù¥ÎÇò ÏøºÎ¶¨ ÏµúÏ†ÅÌôîÎ•º Í≥†Î†§Ìï¥Î≥¥ÏÑ∏Ïöî.');
    }

    return suggestions;
  };

  return (
    <div className="slow-query-component">
      {/* Ìó§Îçî */}
      <div className="component-header">
        <div className="header-left">
          <h2>Ïä¨Î°úÏö∞ ÏøºÎ¶¨ Î∂ÑÏÑù</h2>
          {selectedDb && (
            <span className="selected-db">ÏÑ†ÌÉùÎêú DB: {selectedDb}</span>
          )}
        </div>

        <div className="header-controls">
          {/* DB ÏÑ†ÌÉù */}
          <select 
            value={selectedDb || ''} 
            onChange={(e) => onDbChange(e.target.value)}
            className="db-select"
          >
            <option value="">DB ÏÑ†ÌÉù</option>
            {databases?.map(db => (
              <option key={db.name} value={db.name}>
                {db.name}
              </option>
            ))}
          </select>

          {/* ÏãúÍ∞Ñ Î≤îÏúÑ */}
          <select 
            value={filters.timeRange} 
            onChange={(e) => handleFilterChange('timeRange', e.target.value)}
            className="time-range-select"
          >
            {timeRanges.map(range => (
              <option key={range.value} value={range.value}>
                {range.label}
              </option>
            ))}
          </select>

          {/* ÏµúÏÜå Ïã§Ìñâ ÏãúÍ∞Ñ */}
          <input
            type="number"
            value={filters.minDuration}
            onChange={(e) => handleFilterChange('minDuration', Number(e.target.value))}
            placeholder="ÏµúÏÜå ÏãúÍ∞Ñ(Ï¥à)"
            className="min-duration-input"
            min="0"
            step="0.1"
          />

          {/* Ï†ïÎ†¨ Í∏∞Ï§Ä */}
          <select 
            value={filters.sortBy} 
            onChange={(e) => handleFilterChange('sortBy', e.target.value)}
            className="sort-select"
          >
            {sortOptions.map(option => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>

          {/* Ï†ïÎ†¨ ÏàúÏÑú */}
          <button 
            onClick={() => handleFilterChange('sortOrder', filters.sortOrder === 'desc' ? 'asc' : 'desc')}
            className="sort-order-btn"
          >
            {filters.sortOrder === 'desc' ? '‚Üì' : '‚Üë'}
          </button>

          {/* ÏÉàÎ°úÍ≥†Ïπ® */}
          <button 
            onClick={fetchSlowQueries}
            disabled={loading || !selectedDb}
            className="btn btn-outline"
          >
            {loading ? 'Î°úÎî© Ï§ë...' : 'üîÑ ÏÉàÎ°úÍ≥†Ïπ®'}
          </button>
        </div>
      </div>

      {!selectedDb ? (
        <div className="no-db-selected">
          <p>Î∂ÑÏÑùÌï† Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§Î•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.</p>
        </div>
      ) : (
        <div className="slow-query-content">
          {/* Ïä¨Î°úÏö∞ ÏøºÎ¶¨ Î™©Î°ù */}
          <div className="query-list">
            <h3>Ïä¨Î°úÏö∞ ÏøºÎ¶¨ Î™©Î°ù ({slowQueries.length}Í∞ú)</h3>

            {loading ? (
              <div className="loading">Ïä¨Î°úÏö∞ ÏøºÎ¶¨ Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏Ïò§Îäî Ï§ë...</div>
            ) : slowQueries.length > 0 ? (
              <div className="query-table-container">
                <table className="query-table">
                  <thead>
                    <tr>
                      <th>Ïã§Ìñâ ÏãúÍ∞Ñ</th>
                      <th>Ïã§Ìñâ ÌöüÏàò</th>
                      <th>ÌèâÍ∑† ÏãúÍ∞Ñ</th>
                      <th>Í≤ÄÏÇ¨Îêú Ìñâ</th>
                      <th>Î∞òÌôòÎêú Ìñâ</th>
                      <th>SQL ÎØ∏Î¶¨Î≥¥Í∏∞</th>
                      <th>ÏûëÏóÖ</th>
                    </tr>
                  </thead>
                  <tbody>
                    {slowQueries.map((query, index) => (
                      <tr 
                        key={index} 
                        className={selectedQuery?.id === query.id ? 'selected' : ''}
                        onClick={() => handleQuerySelect(query)}
                      >
                        <td className="duration-cell">
                          <span className="duration-value">{formatDuration(query.duration)}</span>
                          {query.duration > 10 && <span className="warning-badge">‚ö†Ô∏è</span>}
                        </td>
                        <td>{formatNumber(query.count)}</td>
                        <td>{formatDuration(query.avg_duration)}</td>
                        <td>{formatNumber(query.rows_examined)}</td>
                        <td>{formatNumber(query.rows_sent)}</td>
                        <td className="sql-preview">
                          <code>{query.sql?.substring(0, 100)}...</code>
                        </td>
                        <td>
                          <button 
                            onClick={(e) => {
                              e.stopPropagation();
                              handleQuerySelect(query);
                            }}
                            className="btn btn-sm btn-outline"
                          >
                            ÏÉÅÏÑ∏Î≥¥Í∏∞
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <div className="no-queries">
                <p>Ï°∞Í±¥Ïóê ÎßûÎäî Ïä¨Î°úÏö∞ ÏøºÎ¶¨Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
                <p>ÌïÑÌÑ∞ Ï°∞Í±¥ÏùÑ Ï°∞Ï†ïÌï¥Î≥¥ÏÑ∏Ïöî.</p>
              </div>
            )}
          </div>

          {/* ÏøºÎ¶¨ ÏÉÅÏÑ∏ Ï†ïÎ≥¥ */}
          {showDetails && selectedQuery && (
            <div className="query-details">
              <div className="details-header">
                <h3>ÏøºÎ¶¨ ÏÉÅÏÑ∏ Ï†ïÎ≥¥</h3>
                <button 
                  onClick={() => setShowDetails(false)}
                  className="close-btn"
                >
                  ‚úï
                </button>
              </div>

              <div className="details-content">
                {/* Í∏∞Î≥∏ Ï†ïÎ≥¥ */}
                <div className="detail-section">
                  <h4>Í∏∞Î≥∏ Ï†ïÎ≥¥</h4>
                  <div className="info-grid">
                    <div className="info-item">
                      <label>ÏµúÎåÄ Ïã§Ìñâ ÏãúÍ∞Ñ:</label>
                      <span>{formatDuration(selectedQuery.duration)}</span>
                    </div>
                    <div className="info-item">
                      <label>ÌèâÍ∑† Ïã§Ìñâ ÏãúÍ∞Ñ:</label>
                      <span>{formatDuration(selectedQuery.avg_duration)}</span>
                    </div>
                    <div className="info-item">
                      <label>Ïã§Ìñâ ÌöüÏàò:</label>
                      <span>{formatNumber(selectedQuery.count)}</span>
                    </div>
                    <div className="info-item">
                      <label>Í≤ÄÏÇ¨Îêú Ìñâ Ïàò:</label>
                      <span>{formatNumber(selectedQuery.rows_examined)}</span>
                    </div>
                    <div className="info-item">
                      <label>Î∞òÌôòÎêú Ìñâ Ïàò:</label>
                      <span>{formatNumber(selectedQuery.rows_sent)}</span>
                    </div>
                    <div className="info-item">
                      <label>Ìö®Ïú®ÏÑ±:</label>
                      <span className={`efficiency ${selectedQuery.rows_examined > selectedQuery.rows_sent * 10 ? 'poor' : 'good'}`}>
                        {((selectedQuery.rows_sent / selectedQuery.rows_examined) * 100).toFixed(1)}%
                      </span>
                    </div>
                  </div>
                </div>

                {/* SQL ÏøºÎ¶¨ */}
                <div className="detail-section">
                  <h4>SQL ÏøºÎ¶¨</h4>
                  <div className="sql-container">
                    <pre><code>{formatSQL(selectedQuery.sql)}</code></pre>
                    <button 
                      onClick={() => navigator.clipboard.writeText(selectedQuery.sql)}
                      className="copy-btn"
                    >
                      Î≥µÏÇ¨
                    </button>
                  </div>
                </div>

                {/* ÏµúÏ†ÅÌôî Ï†úÏïà */}
                <div className="detail-section">
                  <h4>ÏµúÏ†ÅÌôî Ï†úÏïà</h4>
                  <div className="suggestions">
                    {analyzeQuery(selectedQuery).map((suggestion, index) => (
                      <div key={index} className="suggestion-item">
                        <span className="suggestion-icon">üí°</span>
                        <span>{suggestion}</span>
                      </div>
                    ))}
                    {analyzeQuery(selectedQuery).length === 0 && (
                      <p className="no-suggestions">ÌòÑÏû¨ ÏøºÎ¶¨Ïóê ÎåÄÌïú ÌäπÎ≥ÑÌïú ÏµúÏ†ÅÌôî Ï†úÏïàÏù¥ ÏóÜÏäµÎãàÎã§.</p>
                    )}
                  </div>
                </div>

                {/* Ïã§Ìñâ Í≥ÑÌöç (ÏûàÎäî Í≤ΩÏö∞) */}
                {queryDetails?.execution_plan && (
                  <div className="detail-section">
                    <h4>Ïã§Ìñâ Í≥ÑÌöç</h4>
                    <div className="execution-plan">
                      <pre><code>{JSON.stringify(queryDetails.execution_plan, null, 2)}</code></pre>
                    </div>
                  </div>
                )}

                {/* ÏãúÍ∞ÑÎ≥Ñ Ïã§Ìñâ ÌÜµÍ≥Ñ */}
                {queryDetails?.time_stats && (
                  <div className="detail-section">
                    <h4>ÏãúÍ∞ÑÎ≥Ñ Ïã§Ìñâ ÌÜµÍ≥Ñ</h4>
                    <div className="time-stats">
                      <table className="stats-table">
                        <thead>
                          <tr>
                            <th>ÏãúÍ∞ÑÎåÄ</th>
                            <th>Ïã§Ìñâ ÌöüÏàò</th>
                            <th>ÌèâÍ∑† ÏãúÍ∞Ñ</th>
                            <th>ÏµúÎåÄ ÏãúÍ∞Ñ</th>
                          </tr>
                        </thead>
                        <tbody>
                          {queryDetails.time_stats.map((stat, index) => (
                            <tr key={index}>
                              <td>{stat.hour}:00</td>
                              <td>{stat.count}</td>
                              <td>{formatDuration(stat.avg_duration)}</td>
                              <td>{formatDuration(stat.max_duration)}</td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default SlowQueryComponent;
