      try {
        await axios.delete(`/api/conversations/${convId}`);
        // ëŒ€í™” ëª©ë¡ ìƒˆë¡œê³ ì¹¨
        const convRes = await axios.get(`/api/conversations?db_name=${selectedDb}`);
