          if (convRes.data.status === 'success') {
            setConversations(convRes.data.conversations);
          }
        }
      } catch (error) {
        console.error('ì±„íŒ… ì´ˆê¸°í™” ì˜¤ë¥˜:', error);
        alert('ì±„íŒ… ì´ˆê¸°í™”ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.');
