                    <div className="conversation-meta">
                      <small>{new Date(conv.updated_at).toLocaleString()}</small>
                    </div>
                  </div>
                  <button 
                    className="btn btn-sm btn-outline-danger delete-btn"
                    onClick={(e) => { 
                      e.stopPropagation(); 
                      handleDeleteConversation(conv.id); 
                    }}
