// DatabaseManager.js
// DB Ïó∞Í≤∞ Í¥ÄÎ¶¨, Ìèº, Î¶¨Ïä§Ìä∏, Ïù∏ÎùºÏù∏ ÏàòÏ†ï Îì±
import React, { useState, useEffect } from 'react';

function DatabaseManager({ databases, onDatabaseChange, onDatabaseDelete, onDatabaseAdd }) {
  const [showForm, setShowForm] = useState(false);
  const [editingDb, setEditingDb] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    host: '',
    port: '',
    database: '',
    username: '',
    password: '',
  });
  const [errors, setErrors] = useState({});

  // Ïó∞Í≤∞ ÏÉÅÌÉú Í¥ÄÎ¶¨ (localStorage Ïó∞Îèô)
  const [connectionStatus, setConnectionStatus] = useState(() => {
    try {
      const saved = localStorage.getItem('db-connection-status');
      return saved ? JSON.parse(saved) : {};
    } catch {
      return {};
    }
  });

  // connectionStatusÍ∞Ä Î∞îÎÄî ÎïåÎßàÎã§ localStorageÏóê Ï†ÄÏû•
  useEffect(() => {
    localStorage.setItem('db-connection-status', JSON.stringify(connectionStatus));
  }, [connectionStatus]);

  // 1Î∂ÑÎßàÎã§ ÏûêÎèô Ïó∞Í≤∞ ÏÉÅÌÉú Í∞±Ïã†
  useEffect(() => {
    let timer;
    const pollConnections = async () => {
      if (databases && databases.length > 0) {
        for (const db of databases) {
          try {
            const response = await fetch('/api/databases/test', {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify(db),
              credentials: 'include'
            });
            const result = await response.json();
            setConnectionStatus(prev => ({
              ...prev,
              [db.name]: result.success ? 'success' : 'fail'
            }));
          } catch {
            setConnectionStatus(prev => ({
              ...prev,
              [db.name]: 'fail'
            }));
          }
        }
      }
      timer = setTimeout(pollConnections, 60000); // 1Î∂ÑÎßàÎã§ Î∞òÎ≥µ
    };
    pollConnections();
    return () => clearTimeout(timer);
  }, [databases]);

  const [showBrowseForm, setShowBrowseForm] = useState(false);
  const [browseFormData, setBrowseFormData] = useState({
    host: '',
    port: '',
    username: '',
    password: '',
  });
  const [browsedDatabases, setBrowsedDatabases] = useState([]);
  const [browseError, setBrowseError] = useState('');

  // Ìèº Ï¥àÍ∏∞Ìôî
  const resetForm = () => {
    setFormData({
      name: '',
      host: '',
      port: '',
      database: '',
      username: '',
      password: '',
    });
    setErrors({});
    setEditingDb(null);
  };

  // Ìèº ÌëúÏãú/Ïà®ÍπÄ ÌÜ†Í∏Ä
  const toggleForm = () => {
    if (showForm) {
      resetForm();
    }
    setShowForm(!showForm);
    setShowBrowseForm(false); // Hide browse form when toggling add/edit form
  };

  // DB Browse Ìèº ÌëúÏãú/Ïà®ÍπÄ ÌÜ†Í∏Ä
  const toggleBrowseForm = () => {
    setShowBrowseForm(!showBrowseForm);
    setShowForm(false); // Hide add/edit form when toggling browse form
    setBrowsedDatabases([]); // Clear browsed results
    setBrowseError('');
    setBrowseFormData({
      host: formData.host || '',
      port: formData.port || '',
      username: formData.username || '',
      password: formData.password || '',
    });
  };

  // Ìé∏Ïßë Î™®Îìú ÏãúÏûë
  const startEdit = (db) => {
    setFormData({
      name: db.name,
      host: db.host,
      port: db.port || '',
      database: db.dbname || '', // Use dbname from backend
      username: db.user || '',   // Use user from backend
      password: db.password,
    });
    setEditingDb(db);
    setShowForm(true);
    setShowBrowseForm(false); // Hide browse form
    setErrors({});
  };

  // ÏûÖÎ†• ÌïÑÎìú Î≥ÄÍ≤Ω Ï≤òÎ¶¨
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // ÏóêÎü¨ ÌÅ¥Î¶¨Ïñ¥
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  // DB Browse Ìèº ÏûÖÎ†• ÌïÑÎìú Î≥ÄÍ≤Ω Ï≤òÎ¶¨
  const handleBrowseInputChange = (e) => {
    const { name, value } = e.target;
    setBrowseFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Ï°∞ÌöåÎêú DB ÏÑ†ÌÉù
  const handleSelectBrowsedDb = (dbName) => {
    setFormData(prev => ({
      ...prev,
      database: dbName
    }));
    setShowBrowseForm(false);
    setShowForm(true);
  };

  // Ìèº Ïú†Ìö®ÏÑ± Í≤ÄÏÇ¨
  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'DB Ïù¥Î¶ÑÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî';
    }

    if (!formData.host.trim()) {
      newErrors.host = 'Ìò∏Ïä§Ìä∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî';
    }

    if (!formData.database.trim()) {
      newErrors.database = 'Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§Î™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî';
    }

    if (!formData.username.trim()) {
      newErrors.username = 'ÏÇ¨Ïö©ÏûêÎ™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî';
    }

    if (!formData.password.trim()) {
      newErrors.password = 'ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî';
    }

    // Ìè¨Ìä∏ Î≤àÌò∏ Í≤ÄÏ¶ù
    if (formData.port && (isNaN(formData.port) || formData.port < 1 || formData.port > 65535)) {
      newErrors.port = 'Ïú†Ìö®Ìïú Ìè¨Ìä∏ Î≤àÌò∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî (1-65535)';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // DB Î™©Î°ù Ï°∞Ìöå Ï≤òÎ¶¨
  const handleBrowseDatabases = async (e) => {
    e.preventDefault();
    setBrowseError('');
    setBrowsedDatabases([]);

    if (!browseFormData.host.trim() || !browseFormData.username.trim() || !browseFormData.password.trim()) {
      setBrowseError('Ìò∏Ïä§Ìä∏, ÏÇ¨Ïö©ÏûêÎ™Ö, ÎπÑÎ∞ÄÎ≤àÌò∏Îäî ÌïÑÏàòÏûÖÎãàÎã§.');
      return;
    }

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('host', browseFormData.host);
      formDataToSend.append('port', browseFormData.port || 5432);
      formDataToSend.append('user', browseFormData.username);
      formDataToSend.append('password', browseFormData.password);

      const response = await fetch('/api/databases/browse', {
        method: 'POST',
        body: formDataToSend,
      });

      const result = await response.json();
      if (result.status === 'success') {
        setBrowsedDatabases(result.databases);
      } else {
        setBrowseError(result.message || 'Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Î™©Î°ù Ï°∞ÌöåÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    } catch (error) {
      setBrowseError('Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Î™©Î°ù Ï°∞Ìöå Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      console.error('Browse DBs error:', error);
    }
  };

  // Ìèº Ï†úÏ∂ú Ï≤òÎ¶¨
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('name', formData.name);
      formDataToSend.append('host', formData.host);
      formDataToSend.append('port', formData.port);
      formDataToSend.append('user', formData.username);
      formDataToSend.append('password', formData.password);
      formDataToSend.append('dbname', formData.database);

      const response = await fetch('/api/databases', {
        method: 'POST',
        body: formDataToSend
      });

      if (response.ok) {
        const result = await response.json();
        if (result.status === 'success') {
          onDatabaseChange(); // Î∂ÄÎ™® Ïª¥Ìè¨ÎÑåÌä∏Ïóê Î≥ÄÍ≤Ω ÏïåÎ¶º
          resetForm();
          setShowForm(false);
        } else {
          setErrors({ general: 'Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.' });
        }
      } else {
        throw new Error('ÏöîÏ≤≠ Ïã§Ìå®');
      }
    } catch (error) {
      setErrors({ general: 'Ïó∞Í≤∞ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.' });
    }
  };

  // DB ÏÇ≠Ï†ú Ï≤òÎ¶¨
  const handleDelete = async (dbName) => {
    if (!window.confirm(`Ï†ïÎßêÎ°ú "${dbName}" Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Ïó∞Í≤∞ÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?`)) {
      return;
    }

    try {
      const response = await fetch(`/api/databases/${dbName}`, {
        method: 'DELETE'
      });

      if (response.ok) {
        const result = await response.json();
        if (result.status === 'success') {
          onDatabaseDelete(dbName);
        } else {
          alert('ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
        }
      } else {
        throw new Error('ÏöîÏ≤≠ Ïã§Ìå®');
      }
    } catch (error) {
      alert('ÏÇ≠Ï†ú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    }
  };

  // Ïó∞Í≤∞ ÌÖåÏä§Ìä∏
  const testConnection = async (db) => {
    try {
      setConnectionStatus(prev => ({ ...prev, [db.name]: 'loading' }));
      const response = await fetch('/api/databases/test', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(db),
        credentials: 'include'
      });
      const result = await response.json();
      if (result.success) {
        setConnectionStatus(prev => ({ ...prev, [db.name]: 'success' }));
        alert('Ïó∞Í≤∞ ÌÖåÏä§Ìä∏ ÏÑ±Í≥µ!');
      } else {
        setConnectionStatus(prev => ({ ...prev, [db.name]: 'fail' }));
        alert(`Ïó∞Í≤∞ ÌÖåÏä§Ìä∏ Ïã§Ìå®: ${result.message}`);
      }
    } catch (error) {
      setConnectionStatus(prev => ({ ...prev, [db.name]: 'fail' }));
      alert('Ïó∞Í≤∞ ÌÖåÏä§Ìä∏ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    }
  };

  return (
    <div className="database-manager">
      {/* Ìó§Îçî */}
      <div className="manager-header">
        <h2>Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Ïó∞Í≤∞ Í¥ÄÎ¶¨</h2>
        <button 
          onClick={toggleForm} 
          className={`btn ${showForm ? 'btn-secondary' : 'btn-primary'}`}
        >
          {showForm ? 'Ï∑®ÏÜå' : 'ÏÉà DB Ï∂îÍ∞Ä'}
        </button>
        <button 
          onClick={toggleBrowseForm} 
          className={`btn ${showBrowseForm ? 'btn-secondary' : 'btn-info'}`}
        >
          {showBrowseForm ? 'Ï∑®ÏÜå' : 'DB Î™©Î°ù Ï°∞Ìöå'}
        </button>
      </div>

      {/* DB Ï∂îÍ∞Ä/Ìé∏Ïßë Ìèº */}
      {showForm && (
        <div className="form-container">
          <form onSubmit={handleSubmit} className="db-form">
            <h3>{editingDb ? 'DB Ïó∞Í≤∞ ÏàòÏ†ï' : 'ÏÉà DB Ïó∞Í≤∞ Ï∂îÍ∞Ä'}</h3>

            {errors.general && (
              <div className="error-message">{errors.general}</div>
            )}

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="name">DB Ïù¥Î¶Ñ *</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  placeholder="Ïòà: production_db"
                  className={errors.name ? 'error' : ''}
                />
                {errors.name && <span className="error-text">{errors.name}</span>}
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="host">Ìò∏Ïä§Ìä∏ *</label>
                <input
                  type="text"
                  id="host"
                  name="host"
                  value={formData.host}
                  onChange={handleInputChange}
                  placeholder="Ïòà: localhost ÎòêÎäî 192.168.1.100"
                  className={errors.host ? 'error' : ''}
                />
                {errors.host && <span className="error-text">{errors.host}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="port">Ìè¨Ìä∏</label>
                <input
                  type="number"
                  id="port"
                  name="port"
                  value={formData.port}
                  onChange={handleInputChange}
                  placeholder="Í∏∞Î≥∏Í∞í ÏÇ¨Ïö©"
                  className={errors.port ? 'error' : ''}
                />
                {errors.port && <span className="error-text">{errors.port}</span>}
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="database">Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§Î™Ö *</label>
                <input
                  type="text"
                  id="database"
                  name="database"
                  value={formData.database}
                  onChange={handleInputChange}
                  placeholder="Ïòà: myapp_production"
                  className={errors.database ? 'error' : ''}
                />
                {errors.database && <span className="error-text">{errors.database}</span>}
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="username">ÏÇ¨Ïö©ÏûêÎ™Ö *</label>
                <input
                  type="text"
                  id="username"
                  name="username"
                  value={formData.username}
                  onChange={handleInputChange}
                  placeholder="Ïòà: dbuser"
                  className={errors.username ? 'error' : ''}
                />
                {errors.username && <span className="error-text">{errors.username}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="password">ÎπÑÎ∞ÄÎ≤àÌò∏ *</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏ ÏûÖÎ†•"
                  className={errors.password ? 'error' : ''}
                />
                {errors.password && <span className="error-text">{errors.password}</span>}
              </div>
            </div>

            <div className="form-actions">
              <button type="submit" className="btn btn-primary">
                {editingDb ? 'ÏàòÏ†ï' : 'Ï∂îÍ∞Ä'}
              </button>
              <button type="button" onClick={toggleForm} className="btn btn-secondary">
                Ï∑®ÏÜå
              </button>
            </div>
          </form>
        </div>
      )}

      {/* DB Î™©Î°ù Ï°∞Ìöå Ìèº */}
      {showBrowseForm && (
        <div className="form-container">
          <form onSubmit={handleBrowseDatabases} className="db-form">
            <h3>DB Ïù∏Ïä§ÌÑ¥Ïä§ÏóêÏÑú Î™©Î°ù Ï°∞Ìöå</h3>
            {browseError && (
              <div className="error-message">{browseError}</div>
            )}
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="browse-host">Ìò∏Ïä§Ìä∏ *</label>
                <input
                  type="text"
                  id="browse-host"
                  name="host"
                  value={browseFormData.host}
                  onChange={handleBrowseInputChange}
                  placeholder="Ïòà: localhost ÎòêÎäî RDS ÏóîÎìúÌè¨Ïù∏Ìä∏"
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="browse-port">Ìè¨Ìä∏</label>
                <input
                  type="number"
                  id="browse-port"
                  name="port"
                  value={browseFormData.port}
                  onChange={handleBrowseInputChange}
                  placeholder="Í∏∞Î≥∏Í∞í 5432"
                />
              </div>
            </div>
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="browse-username">ÏÇ¨Ïö©ÏûêÎ™Ö *</label>
                <input
                  type="text"
                  id="browse-username"
                  name="username"
                  value={browseFormData.username}
                  onChange={handleBrowseInputChange}
                  placeholder="Ïòà: postgres"
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="browse-password">ÎπÑÎ∞ÄÎ≤àÌò∏ *</label>
                <input
                  type="password"
                  id="browse-password"
                  name="password"
                  value={browseFormData.password}
                  onChange={handleBrowseInputChange}
                  placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏ ÏûÖÎ†•"
                  required
                />
              </div>
            </div>
            <div className="form-actions">
              <button type="submit" className="btn btn-info">
                DB Î™©Î°ù Ï°∞Ìöå
              </button>
              <button type="button" onClick={toggleBrowseForm} className="btn btn-secondary">
                Ï∑®ÏÜå
              </button>
            </div>
          </form>

          {browsedDatabases.length > 0 && (
            <div className="browsed-databases-list">
              <h4>Ï°∞ÌöåÎêú Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ ({browsedDatabases.length}Í∞ú)</h4>
              <ul>
                {browsedDatabases.map((dbName, index) => (
                  <li key={index}>
                    {dbName}
                    <button 
                      onClick={() => handleSelectBrowsedDb(dbName)}
                      className="btn btn-sm btn-outline"
                    >
                      ÏÑ†ÌÉù
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}

      {/* DB Î™©Î°ù */}
      <div className="database-list">
        {databases && databases.length > 0 ? (
          <table className="db-table">
            <thead>
              <tr>
                <th>Ïù¥Î¶Ñ</th>
                <th>Ìò∏Ïä§Ìä∏</th>
                <th>Ìè¨Ìä∏</th>
                <th>DBÎ™Ö</th>
                <th>ÏÇ¨Ïö©Ïûê</th>
                <th>Connection</th>
                <th>Ïï°ÏÖò</th>
              </tr>
            </thead>
            <tbody>
              {databases.map(db => (
                <tr key={db.name}>
                  <td>{db.name}</td>
                  <td>{db.host}</td>
                  <td>{db.port || 'Í∏∞Î≥∏Í∞í'}</td>
                  <td>{db.dbname || 'Î™®Îì† DB'}</td>
                  <td>{db.user}</td>
                  <td>
                    <span className={`conn-indicator ${connectionStatus[db.name]}`}></span>
                    {connectionStatus[db.name] === 'loading' && <span style={{marginLeft:4}}>‚è≥</span>}
                  </td>
                  <td>
                    <button 
                      onClick={() => testConnection(db)}
                      className="btn btn-sm btn-outline"
                      title="Ïó∞Í≤∞ ÌÖåÏä§Ìä∏"
                    >
                      üîó ÌÖåÏä§Ìä∏
                    </button>
                    <button 
                      onClick={() => startEdit(db)}
                      className="btn btn-sm btn-outline"
                      title="ÏàòÏ†ï"
                    >
                      ‚úèÔ∏è ÏàòÏ†ï
                    </button>
                    <button 
                      onClick={() => handleDelete(db.name)}
                      className="btn btn-sm btn-danger"
                      title="ÏÇ≠Ï†ú"
                    >
                      üóëÔ∏è ÏÇ≠Ï†ú
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <div className="empty-state">
            <p>Îì±Î°ùÎêú Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Ïó∞Í≤∞Ïù¥ ÏóÜÏäµÎãàÎã§.</p>
            <p>ÏÉà DB Ïó∞Í≤∞ÏùÑ Ï∂îÍ∞ÄÌï¥Î≥¥ÏÑ∏Ïöî!</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default DatabaseManager;
