// DatabaseManager.js
// DB Ïó∞Í≤∞ Í¥ÄÎ¶¨, Ìèº, Î¶¨Ïä§Ìä∏, Ïù∏ÎùºÏù∏ ÏàòÏ†ï Îì±
import React, { useState, useEffect } from 'react';

function DatabaseManager({ databases, onDatabaseChange, onDatabaseDelete, onDatabaseAdd }) {
  const [showForm, setShowForm] = useState(false);
  const [editingDb, setEditingDb] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    host: '',
    port: '',
    database: '',
    username: '',
    password: '',
    db_type: 'mysql'
  });
  const [errors, setErrors] = useState({});

  const dbTypes = [
    { value: 'mysql', label: 'MySQL' },
    { value: 'postgresql', label: 'PostgreSQL' },
    { value: 'sqlite', label: 'SQLite' },
    { value: 'oracle', label: 'Oracle' },
    { value: 'sqlserver', label: 'SQL Server' }
  ];

  // Ìèº Ï¥àÍ∏∞Ìôî
  const resetForm = () => {
    setFormData({
      name: '',
      host: '',
      port: '',
      database: '',
      username: '',
      password: '',
      db_type: 'mysql'
    });
    setErrors({});
    setEditingDb(null);
  };

  // Ìèº ÌëúÏãú/Ïà®ÍπÄ ÌÜ†Í∏Ä
  const toggleForm = () => {
    if (showForm) {
      resetForm();
    }
    setShowForm(!showForm);
  };

  // Ìé∏Ïßë Î™®Îìú ÏãúÏûë
  const startEdit = (db) => {
    setFormData({
      name: db.name,
      host: db.host,
      port: db.port || '',
      database: db.database,
      username: db.username,
      password: db.password,
      db_type: db.db_type || 'mysql'
    });
    setEditingDb(db);
    setShowForm(true);
    setErrors({});
  };

  // ÏûÖÎ†• ÌïÑÎìú Î≥ÄÍ≤Ω Ï≤òÎ¶¨
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // ÏóêÎü¨ ÌÅ¥Î¶¨Ïñ¥
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  // Ìèº Ïú†Ìö®ÏÑ± Í≤ÄÏÇ¨
  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'DB Ïù¥Î¶ÑÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî';
    }

    if (!formData.host.trim()) {
      newErrors.host = 'Ìò∏Ïä§Ìä∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî';
    }

    if (!formData.database.trim()) {
      newErrors.database = 'Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§Î™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî';
    }

    if (!formData.username.trim()) {
      newErrors.username = 'ÏÇ¨Ïö©ÏûêÎ™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî';
    }

    if (!formData.password.trim()) {
      newErrors.password = 'ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî';
    }

    // Ìè¨Ìä∏ Î≤àÌò∏ Í≤ÄÏ¶ù
    if (formData.port && (isNaN(formData.port) || formData.port < 1 || formData.port > 65535)) {
      newErrors.port = 'Ïú†Ìö®Ìïú Ìè¨Ìä∏ Î≤àÌò∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî (1-65535)';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Ìèº Ï†úÏ∂ú Ï≤òÎ¶¨
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('name', formData.name);
      formDataToSend.append('host', formData.host);
      formDataToSend.append('port', formData.port);
      formDataToSend.append('user', formData.username);
      formDataToSend.append('password', formData.password);
      formDataToSend.append('dbname', formData.database);

      const response = await fetch('/api/databases', {
        method: 'POST',
        body: formDataToSend
      });

      if (response.ok) {
        const result = await response.json();
        if (result.status === 'success') {
          onDatabaseChange(); // Î∂ÄÎ™® Ïª¥Ìè¨ÎÑåÌä∏Ïóê Î≥ÄÍ≤Ω ÏïåÎ¶º
          resetForm();
          setShowForm(false);
        } else {
          setErrors({ general: 'Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.' });
        }
      } else {
        throw new Error('ÏöîÏ≤≠ Ïã§Ìå®');
      }
    } catch (error) {
      setErrors({ general: 'Ïó∞Í≤∞ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.' });
    }
  };

  // DB ÏÇ≠Ï†ú Ï≤òÎ¶¨
  const handleDelete = async (dbName) => {
    if (!window.confirm(`Ï†ïÎßêÎ°ú "${dbName}" Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Ïó∞Í≤∞ÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?`)) {
      return;
    }

    try {
      const response = await fetch(`/api/databases/${dbName}`, {
        method: 'DELETE'
      });

      if (response.ok) {
        const result = await response.json();
        if (result.status === 'success') {
          onDatabaseDelete(dbName);
        } else {
          alert('ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
        }
      } else {
        throw new Error('ÏöîÏ≤≠ Ïã§Ìå®');
      }
    } catch (error) {
      alert('ÏÇ≠Ï†ú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    }
  };

  // Ïó∞Í≤∞ ÌÖåÏä§Ìä∏
  const testConnection = async (db) => {
    try {
      const response = await fetch('/api/databases/test', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(db),
        credentials: 'include'
      });
      const result = await response.json();
      if (result.success) {
        alert('Ïó∞Í≤∞ ÌÖåÏä§Ìä∏ ÏÑ±Í≥µ!');
      } else {
        alert(`Ïó∞Í≤∞ ÌÖåÏä§Ìä∏ Ïã§Ìå®: ${result.message}`);
      }
    } catch (error) {
      alert('Ïó∞Í≤∞ ÌÖåÏä§Ìä∏ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    }
  };

  return (
    <div className="database-manager">
      {/* Ìó§Îçî */}
      <div className="manager-header">
        <h2>Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Ïó∞Í≤∞ Í¥ÄÎ¶¨</h2>
        <button 
          onClick={toggleForm} 
          className={`btn ${showForm ? 'btn-secondary' : 'btn-primary'}`}
        >
          {showForm ? 'Ï∑®ÏÜå' : 'ÏÉà DB Ï∂îÍ∞Ä'}
        </button>
      </div>

      {/* DB Ï∂îÍ∞Ä/Ìé∏Ïßë Ìèº */}
      {showForm && (
        <div className="form-container">
          <form onSubmit={handleSubmit} className="db-form">
            <h3>{editingDb ? 'DB Ïó∞Í≤∞ ÏàòÏ†ï' : 'ÏÉà DB Ïó∞Í≤∞ Ï∂îÍ∞Ä'}</h3>

            {errors.general && (
              <div className="error-message">{errors.general}</div>
            )}

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="name">DB Ïù¥Î¶Ñ *</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  placeholder="Ïòà: production_db"
                  className={errors.name ? 'error' : ''}
                />
                {errors.name && <span className="error-text">{errors.name}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="db_type">DB ÌÉÄÏûÖ</label>
                <select
                  id="db_type"
                  name="db_type"
                  value={formData.db_type}
                  onChange={handleInputChange}
                >
                  {dbTypes.map(type => (
                    <option key={type.value} value={type.value}>
                      {type.label}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="host">Ìò∏Ïä§Ìä∏ *</label>
                <input
                  type="text"
                  id="host"
                  name="host"
                  value={formData.host}
                  onChange={handleInputChange}
                  placeholder="Ïòà: localhost ÎòêÎäî 192.168.1.100"
                  className={errors.host ? 'error' : ''}
                />
                {errors.host && <span className="error-text">{errors.host}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="port">Ìè¨Ìä∏</label>
                <input
                  type="number"
                  id="port"
                  name="port"
                  value={formData.port}
                  onChange={handleInputChange}
                  placeholder="Í∏∞Î≥∏Í∞í ÏÇ¨Ïö©"
                  className={errors.port ? 'error' : ''}
                />
                {errors.port && <span className="error-text">{errors.port}</span>}
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="database">Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§Î™Ö *</label>
                <input
                  type="text"
                  id="database"
                  name="database"
                  value={formData.database}
                  onChange={handleInputChange}
                  placeholder="Ïòà: myapp_production"
                  className={errors.database ? 'error' : ''}
                />
                {errors.database && <span className="error-text">{errors.database}</span>}
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="username">ÏÇ¨Ïö©ÏûêÎ™Ö *</label>
                <input
                  type="text"
                  id="username"
                  name="username"
                  value={formData.username}
                  onChange={handleInputChange}
                  placeholder="Ïòà: dbuser"
                  className={errors.username ? 'error' : ''}
                />
                {errors.username && <span className="error-text">{errors.username}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="password">ÎπÑÎ∞ÄÎ≤àÌò∏ *</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏ ÏûÖÎ†•"
                  className={errors.password ? 'error' : ''}
                />
                {errors.password && <span className="error-text">{errors.password}</span>}
              </div>
            </div>

            <div className="form-actions">
              <button type="submit" className="btn btn-primary">
                {editingDb ? 'ÏàòÏ†ï' : 'Ï∂îÍ∞Ä'}
              </button>
              <button type="button" onClick={toggleForm} className="btn btn-secondary">
                Ï∑®ÏÜå
              </button>
            </div>
          </form>
        </div>
      )}

      {/* DB Î™©Î°ù */}
      <div className="database-list">
        {databases && databases.length > 0 ? (
          <div className="db-grid">
            {databases.map(db => (
              <div key={db.name} className="db-card">
                <div className="db-card-header">
                  <h4>{db.name}</h4>
                  <div className="db-type-badge">{db.db_type || 'mysql'}</div>
                </div>

                <div className="db-card-body">
                  <div className="db-info">
                    <div><strong>Ìò∏Ïä§Ìä∏:</strong> {db.host}</div>
                    <div><strong>Ìè¨Ìä∏:</strong> {db.port || 'Í∏∞Î≥∏Í∞í'}</div>
                    <div><strong>DB:</strong> {db.database}</div>
                    <div><strong>ÏÇ¨Ïö©Ïûê:</strong> {db.username}</div>
                  </div>
                </div>

                <div className="db-card-actions">
                  <button 
                    onClick={() => testConnection(db)}
                    className="btn btn-sm btn-outline"
                    title="Ïó∞Í≤∞ ÌÖåÏä§Ìä∏"
                  >
                    üîó ÌÖåÏä§Ìä∏
                  </button>
                  <button 
                    onClick={() => startEdit(db)}
                    className="btn btn-sm btn-outline"
                    title="ÏàòÏ†ï"
                  >
                    ‚úèÔ∏è ÏàòÏ†ï
                  </button>
                  <button 
                    onClick={() => handleDelete(db.name)}
                    className="btn btn-sm btn-danger"
                    title="ÏÇ≠Ï†ú"
                  >
                    üóëÔ∏è ÏÇ≠Ï†ú
                  </button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="empty-state">
            <p>Îì±Î°ùÎêú Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Ïó∞Í≤∞Ïù¥ ÏóÜÏäµÎãàÎã§.</p>
            <p>ÏÉà DB Ïó∞Í≤∞ÏùÑ Ï∂îÍ∞ÄÌï¥Î≥¥ÏÑ∏Ïöî!</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default DatabaseManager;
