import React, { useState, useEffect } from 'react';

const OpenAIManager = ({ openaiKeys, selectedKey, onOpenAIKeyChange }) => {
  const [newKeyName, setNewKeyName] = useState('');
  const [newKeyValue, setNewKeyValue] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // 키 추가
  const handleAddKey = async (e) => {
    e.preventDefault();
    if (!newKeyName.trim() || !newKeyValue.trim()) {
      setError('키 이름과 값은 필수입니다.');
      return;
    }

    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('name', newKeyName);
      formData.append('key', newKeyValue);

      const response = await fetch('/api/openai/keys', {
        method: 'POST',
        body: formData,
        credentials: 'include'
      });

      if (response.ok) {
        onOpenAIKeyChange(); // Notify parent to refresh keys
        setNewKeyName('');
        setNewKeyValue('');
        setSuccess('OpenAI API 키가 성공적으로 추가되었습니다.');
        setError('');
      } else {
        setError('키 추가에 실패했습니다.');
      }
    } catch (error) {
      setError('키 추가 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  // 키 선택
  const handleSelectKey = async (keyName) => {
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('name', keyName);

      const response = await fetch('/api/openai/select', {
        method: 'POST',
        body: formData,
        credentials: 'include'
      });

      if (response.ok) {
        onOpenAIKeyChange(); // Notify parent to refresh keys
        setSuccess('OpenAI API 키가 선택되었습니다.');
        setError('');
      } else {
        setError('키 선택에 실패했습니다.');
      }
    } catch (error) {
      setError('키 선택 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  // 키 삭제
  const handleDeleteKey = async (keyName) => {
    if (!window.confirm(`정말로 "${keyName}" 키를 삭제하시겠습니까?`)) {
      return;
    }

    setLoading(true);
    try {
      const response = await fetch(`/api/openai/keys/${keyName}`, {
        method: 'DELETE',
        credentials: 'include'
      });

      if (response.ok) {
        onOpenAIKeyChange(); // Notify parent to refresh keys
        setSuccess('OpenAI API 키가 삭제되었습니다.');
        setError('');
      } else {
        setError('키 삭제에 실패했습니다.');
      }
    } catch (error) {
      setError('키 삭제 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  // 키 마스킹 함수 (앞 3글자 + ... + 뒤 4글자만 보이게)
  const maskKey = (key) => {
    if (!key) return '';
    if (key.length <= 8) return '*'.repeat(key.length);
    return key.substring(0, 3) + '***...***' + key.substring(key.length - 4);
  };

  return (
    <div className="openai-manager">
      <div className="section-header">
        <h2>🔑 OpenAI API 키 관리</h2>
        <p>자연어→SQL 변환을 위한 OpenAI API 키를 등록하고 관리하세요.</p>
      </div>

      {/* 상태 메시지 */}
      {error && (
        <div className="alert alert-error">
          <span>⚠️ {error}</span>
          <button onClick={() => setError('')} className="close-btn">✕</button>
        </div>
      )}

      {success && (
        <div className="alert alert-success">
          <span>✅ {success}</span>
          <button onClick={() => setSuccess('')} className="close-btn">✕</button>
        </div>
      )}

      {/* 키 추가 폼 */}
      <div className="card">
        <h3>새 API 키 추가</h3>
        <form onSubmit={handleAddKey} className="form">
          <div className="form-group">
            <label htmlFor="key-name">키 이름:</label>
            <input
              type="text"
              id="key-name"
              value={newKeyName}
              onChange={(e) => setNewKeyName(e.target.value)}
              placeholder="예: Production Key, Development Key"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="key-value">API 키:</label>
            <input
              type="password"
              id="key-value"
              value={newKeyValue}
              onChange={(e) => setNewKeyValue(e.target.value)}
              placeholder="sk-..."
              required
            />
          </div>

          <button 
            type="submit" 
            className="btn btn-primary"
            disabled={loading}
          >
            {loading ? '추가 중...' : '키 추가'}
          </button>
        </form>
      </div>

      {/* 등록된 키 목록 */}
      <div className="card">
        <h3>등록된 API 키</h3>
        {openaiKeys.length === 0 ? (
          <div className="empty-state">
            <p>등록된 OpenAI API 키가 없습니다.</p>
            <p>위에서 새 키를 추가해주세요.</p>
          </div>
        ) : (
          <div className="key-list">
            {(openaiKeys ?? []).map((key, index) => (
              <div 
                key={index} 
                className={`key-item ${selectedKey === key.name ? 'selected' : ''}`}
              >
                <div className="key-info">
                  <div className="key-name">
                    <strong>{key.name}</strong>
                    {selectedKey === key.name && (
                      <span className="selected-badge">선택됨</span>
                    )}
                  </div>
                  <div className="key-value">
                    {maskKey(key.api_key)}
                  </div>
                </div>
                <div className="key-actions">
                  {selectedKey !== key.name && (
                    <button
                      onClick={() => handleSelectKey(key.name)}
                      className="btn btn-sm btn-outline"
                      disabled={loading}
                    >
                      선택
                    </button>
                  )}
                  <button
                    onClick={() => handleDeleteKey(key.name)}
                    className="btn btn-sm btn-danger"
                    disabled={loading}
                  >
                    삭제
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* 사용법 안내 */}
      <div className="card">
        <h3>📖 사용법</h3>
        <div className="usage-guide">
          <ol>
            <li>OpenAI에서 API 키를 발급받으세요 (<a href="https://platform.openai.com/api-keys" target="_blank" rel="noopener noreferrer">https://platform.openai.com/api-keys</a>)</li>
            <li>위 폼에서 키 이름과 API 키를 입력하고 추가하세요</li>
            <li>사용할 키를 선택하세요</li>
            <li>AI 채팅에서 자연어로 데이터베이스를 질의할 수 있습니다</li>
          </ol>
        </div>
      </div>
    </div>
  );
};

export default OpenAIManager;
