// MonitoringComponent.js
// DBÎ≥Ñ Ïã§ÏãúÍ∞Ñ Î™®ÎãàÌÑ∞ÎßÅ, Ï∞®Ìä∏, ÏÉÅÏÑ∏ ÌéòÏù¥ÏßÄ
import React, { useState, useEffect, useRef } from 'react';

function MonitoringComponent({ selectedDb, databases, onDbChange }) {
  const [metrics, setMetrics] = useState({});
  const [loading, setLoading] = useState(false);
  const [timeRange, setTimeRange] = useState('1h'); // 1h, 6h, 24h, 7d
  const [refreshInterval, setRefreshInterval] = useState(30); // Ï¥à Îã®ÏúÑ
  const [isAutoRefresh, setIsAutoRefresh] = useState(true);
  const intervalRef = useRef(null);

  const timeRanges = [
    { value: '1h', label: '1ÏãúÍ∞Ñ' },
    { value: '6h', label: '6ÏãúÍ∞Ñ' },
    { value: '24h', label: '24ÏãúÍ∞Ñ' },
    { value: '7d', label: '7Ïùº' }
  ];

  const refreshIntervals = [
    { value: 10, label: '10Ï¥à' },
    { value: 30, label: '30Ï¥à' },
    { value: 60, label: '1Î∂Ñ' },
    { value: 300, label: '5Î∂Ñ' }
  ];

  // Î©îÌä∏Î¶≠ Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchMetrics = async () => {
    if (!selectedDb) return;

    setLoading(true);
    try {
      const response = await fetch(`/monitoring/metrics?db_name=${selectedDb}&time_range=${timeRange}`);
      if (response.ok) {
        const data = await response.json();
        setMetrics(data);
      } else {
        console.error('Î©îÌä∏Î¶≠ Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®');
      }
    } catch (error) {
      console.error('Î©îÌä∏Î¶≠ ÏöîÏ≤≠ Ïò§Î•ò:', error);
    } finally {
      setLoading(false);
    }
  };

  // ÏûêÎèô ÏÉàÎ°úÍ≥†Ïπ® ÏÑ§Ï†ï
  useEffect(() => {
    if (isAutoRefresh && selectedDb) {
      fetchMetrics(); // Ï¥àÍ∏∞ Î°úÎìú

      intervalRef.current = setInterval(() => {
        fetchMetrics();
      }, refreshInterval * 1000);

      return () => {
        if (intervalRef.current) {
          clearInterval(intervalRef.current);
        }
      };
    } else {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    }
  }, [selectedDb, timeRange, refreshInterval, isAutoRefresh]);

  // ÏàòÎèô ÏÉàÎ°úÍ≥†Ïπ®
  const handleManualRefresh = () => {
    fetchMetrics();
  };

  // Ï∞®Ìä∏ Îç∞Ïù¥ÌÑ∞ Ìè¨Îß∑ÌåÖ
  const formatChartData = (data, label) => {
    if (!data || !Array.isArray(data)) return [];

    return data.map((point, index) => ({
      x: new Date(point.timestamp || Date.now() - (data.length - index) * 60000),
      y: point.value || 0
    }));
  };

  // ÏÉÅÌÉúÎ≥Ñ ÏÉâÏÉÅ
  const getStatusColor = (status) => {
    switch (status?.toLowerCase()) {
      case 'healthy':
      case 'ok':
        return '#28a745';
      case 'warning':
        return '#ffc107';
      case 'critical':
      case 'error':
        return '#dc3545';
      default:
        return '#6c757d';
    }
  };

  // Î©îÌä∏Î¶≠ Ïπ¥Îìú Î†åÎçîÎßÅ
  const renderMetricCard = (title, value, unit = '', status = null, trend = null) => (
    <div className="metric-card">
      <div className="metric-header">
        <h4>{title}</h4>
        {status && (
          <span 
            className="status-badge"
            style={{ backgroundColor: getStatusColor(status) }}
          >
            {status}
          </span>
        )}
      </div>
      <div className="metric-value">
        <span className="value">{value}</span>
        {unit && <span className="unit">{unit}</span>}
      </div>
      {trend && (
        <div className="metric-trend">
          <span className={`trend-${trend.direction}`}>
            {trend.direction === 'up' ? '‚Üó' : '‚Üò'} {trend.value}%
          </span>
        </div>
      )}
    </div>
  );

  // Ï∞®Ìä∏ Î†åÎçîÎßÅ (Í∞ÑÎã®Ìïú ÎùºÏù∏ Ï∞®Ìä∏)
  const renderChart = (title, data, color = '#007bff') => (
    <div className="chart-card">
      <h4>{title}</h4>
      <div className="chart-container">
        <svg width="100%" height="200" viewBox="0 0 400 200">
          {data.length > 1 && (
            <polyline
              fill="none"
              stroke={color}
              strokeWidth="2"
              points={data.map((point, i) => 
                `${(i / (data.length - 1)) * 400},${200 - (point.y / Math.max(...data.map(p => p.y))) * 180}`
              ).join(' ')}
            />
          )}
          {data.map((point, i) => (
            <circle
              key={i}
              cx={(i / (data.length - 1)) * 400}
              cy={200 - (point.y / Math.max(...data.map(p => p.y))) * 180}
              r="3"
              fill={color}
            />
          ))}
        </svg>
      </div>
    </div>
  );

  return (
    <div className="monitoring-component">
      {/* Ìó§Îçî */}
      <div className="monitoring-header">
        <div className="header-left">
          <h2>Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Î™®ÎãàÌÑ∞ÎßÅ</h2>
          {selectedDb && (
            <span className="selected-db">ÏÑ†ÌÉùÎêú DB: {selectedDb}</span>
          )}
        </div>

        <div className="header-controls">
          {/* DB ÏÑ†ÌÉù */}
          <select 
            value={selectedDb || ''} 
            onChange={(e) => onDbChange(e.target.value)}
            className="db-select"
          >
            <option value="">DB ÏÑ†ÌÉù</option>
            {databases?.map(db => (
              <option key={db.name} value={db.name}>
                {db.name}
              </option>
            ))}
          </select>

          {/* ÏãúÍ∞Ñ Î≤îÏúÑ */}
          <select 
            value={timeRange} 
            onChange={(e) => setTimeRange(e.target.value)}
            className="time-range-select"
          >
            {timeRanges.map(range => (
              <option key={range.value} value={range.value}>
                {range.label}
              </option>
            ))}
          </select>

          {/* ÏÉàÎ°úÍ≥†Ïπ® Í∞ÑÍ≤© */}
          <select 
            value={refreshInterval} 
            onChange={(e) => setRefreshInterval(Number(e.target.value))}
            className="refresh-interval-select"
          >
            {refreshIntervals.map(interval => (
              <option key={interval.value} value={interval.value}>
                {interval.label}
              </option>
            ))}
          </select>

          {/* ÏûêÎèô ÏÉàÎ°úÍ≥†Ïπ® ÌÜ†Í∏Ä */}
          <label className="auto-refresh-toggle">
            <input
              type="checkbox"
              checked={isAutoRefresh}
              onChange={(e) => setIsAutoRefresh(e.target.checked)}
            />
            ÏûêÎèô ÏÉàÎ°úÍ≥†Ïπ®
          </label>

          {/* ÏàòÎèô ÏÉàÎ°úÍ≥†Ïπ® */}
          <button 
            onClick={handleManualRefresh}
            disabled={loading || !selectedDb}
            className="btn btn-outline"
          >
            {loading ? 'ÏÉàÎ°úÍ≥†Ïπ® Ï§ë...' : 'üîÑ ÏÉàÎ°úÍ≥†Ïπ®'}
          </button>
        </div>
      </div>

      {!selectedDb ? (
        <div className="no-db-selected">
          <p>Î™®ÎãàÌÑ∞ÎßÅÌï† Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§Î•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.</p>
        </div>
      ) : (
        <div className="monitoring-content">
          {/* Ï£ºÏöî Î©îÌä∏Î¶≠ Ïπ¥ÎìúÎì§ */}
          <div className="metrics-grid">
            {renderMetricCard(
              'Ïó∞Í≤∞ Ïàò',
              metrics.connections?.current || 0,
              'Í∞ú',
              metrics.connections?.status,
              metrics.connections?.trend
            )}

            {renderMetricCard(
              'ÏøºÎ¶¨/Ï¥à',
              metrics.qps?.current || 0,
              'QPS',
              metrics.qps?.status,
              metrics.qps?.trend
            )}

            {renderMetricCard(
              'Ïä¨Î°úÏö∞ ÏøºÎ¶¨',
              metrics.slow_queries?.current || 0,
              'Í∞ú',
              metrics.slow_queries?.status,
              metrics.slow_queries?.trend
            )}

            {renderMetricCard(
              'CPU ÏÇ¨Ïö©Î•†',
              metrics.cpu?.current || 0,
              '%',
              metrics.cpu?.status,
              metrics.cpu?.trend
            )}

            {renderMetricCard(
              'Î©îÎ™®Î¶¨ ÏÇ¨Ïö©Î•†',
              metrics.memory?.current || 0,
              '%',
              metrics.memory?.status,
              metrics.memory?.trend
            )}

            {renderMetricCard(
              'ÎîîÏä§ÌÅ¨ ÏÇ¨Ïö©Î•†',
              metrics.disk?.current || 0,
              '%',
              metrics.disk?.status,
              metrics.disk?.trend
            )}
          </div>

          {/* Ï∞®Ìä∏ ÏòÅÏó≠ */}
          <div className="charts-grid">
            {metrics.connections?.history && renderChart(
              'Ïó∞Í≤∞ Ïàò Ï∂îÏù¥',
              formatChartData(metrics.connections.history),
              '#007bff'
            )}

            {metrics.qps?.history && renderChart(
              'ÏøºÎ¶¨/Ï¥à Ï∂îÏù¥',
              formatChartData(metrics.qps.history),
              '#28a745'
            )}

            {metrics.cpu?.history && renderChart(
              'CPU ÏÇ¨Ïö©Î•† Ï∂îÏù¥',
              formatChartData(metrics.cpu.history),
              '#ffc107'
            )}

            {metrics.memory?.history && renderChart(
              'Î©îÎ™®Î¶¨ ÏÇ¨Ïö©Î•† Ï∂îÏù¥',
              formatChartData(metrics.memory.history),
              '#dc3545'
            )}
          </div>

          {/* ÏÉÅÏÑ∏ Ï†ïÎ≥¥ */}
          <div className="detailed-metrics">
            <h3>ÏÉÅÏÑ∏ Î©îÌä∏Î¶≠</h3>
            <div className="detailed-grid">
              {metrics.table_stats && (
                <div className="detail-card">
                  <h4>ÌÖåÏù¥Î∏î ÌÜµÍ≥Ñ</h4>
                  <table className="detail-table">
                    <thead>
                      <tr>
                        <th>ÌÖåÏù¥Î∏îÎ™Ö</th>
                        <th>Ìñâ Ïàò</th>
                        <th>ÌÅ¨Í∏∞</th>
                        <th>ÎßàÏßÄÎßâ ÏóÖÎç∞Ïù¥Ìä∏</th>
                      </tr>
                    </thead>
                    <tbody>
                      {metrics.table_stats.map((table, index) => (
                        <tr key={index}>
                          <td>{table.name}</td>
                          <td>{table.rows?.toLocaleString()}</td>
                          <td>{table.size}</td>
                          <td>{table.last_update}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}

              {metrics.process_list && (
                <div className="detail-card">
                  <h4>ÌòÑÏû¨ ÌîÑÎ°úÏÑ∏Ïä§</h4>
                  <table className="detail-table">
                    <thead>
                      <tr>
                        <th>ID</th>
                        <th>ÏÇ¨Ïö©Ïûê</th>
                        <th>Ìò∏Ïä§Ìä∏</th>
                        <th>DB</th>
                        <th>Î™ÖÎ†π</th>
                        <th>ÏãúÍ∞Ñ</th>
                        <th>ÏÉÅÌÉú</th>
                      </tr>
                    </thead>
                    <tbody>
                      {metrics.process_list.map((process, index) => (
                        <tr key={index}>
                          <td>{process.id}</td>
                          <td>{process.user}</td>
                          <td>{process.host}</td>
                          <td>{process.db}</td>
                          <td>{process.command}</td>
                          <td>{process.time}s</td>
                          <td>
                            <span 
                              className="status-badge"
                              style={{ backgroundColor: getStatusColor(process.state) }}
                            >
                              {process.state}
                            </span>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default MonitoringComponent;
